00136 // Construction of the logical volumes for cladding and Fuel and water in the active area
00137 // 1 Hexagon + 2 concentric cylinders for WWR-M2 (or VVR-M2)
00139 void TMXFuel::Build_WWR_M2(G4double RotAnglePolygon, TMXInputParameters* p,
00140                                   G4LogicalVolume* G4LogicCladdingUnion    , G4Material* G4CladdingMaterial,
00141                                   G4LogicalVolume* G4LogicWaterFuelCladding, G4Material* G4WaterFuelMaterial,
00142                                   G4LogicalVolume* G4LogicFuelUnion        , G4Material* G4FuelMaterial,
00143                                   std::vector<G4LogicalVolume*>& G4LogicFuel,
00144                                   std::vector<std::string>& vec_LVname )  // Definition of the logical volumes for cladding and Fuel in the active area VVR-M2
00145 {
00146   G4double dbl_width         = p->dbl_PolygoneWidth; // width between 2 opposite sides, not the maximum of of the polygon
00147   G4double dbl_CladPitch     =  3.  *mm;            // Distance between 2 cylinders
00148   G4double dbl_CladThickness =  2.5 *mm;            // Total thickness of cladding (with fuel inside)
00149   G4double dbl_Clad2diam     = 22.  *mm;            // External diameter of cladding 2
00150   G4double dbl_WallThickness =  0.75*mm;            // Thickness of cladding wall only
00151 
00152   // Hexagon/cylinders parameters
00153   G4int int_Nsides = 6; // Side   number for the polygon
00154   G4int int_NZplan = 2; // Z-plan number for the polygon
00155   G4double* dbl_z_coord = new double[int_NZplan];
00156   G4double* dbl_rin     = new double[int_NZplan];
00157   G4double* dbl_rext    = new double[int_NZplan];
00158   G4double dbl_CladRint;                                   // internal radius
00159   G4double dbl_CladRext;                                   // external radius
00160   G4double dbl_active_l[2]   = {2 * p->CladdingZ, 2 * p->FuelZ}; // Height for Cladding or Fuel
00161   std::string str_name[2]    = {"Cladding", "Fuel"};
00162   
00163   
00164   for( int k = 0; k < 2; ++k) { // k=0 >> Cladding and k=1 >> Fuel
00165       
00166     G4double dbl_max_ext_width = dbl_width - 2 * k * dbl_WallThickness;
00167     G4double dbl_max_int_width = dbl_max_ext_width - 2* (dbl_CladThickness - 2 * k * dbl_WallThickness);
00168     
00169     // Parameter definition to implement hexagon geometry
00170     for(int i=0; i<int_NZplan; ++i)  {
00171         dbl_z_coord[i] = (i-0.5) * dbl_active_l[k];
00172         dbl_rin[i]  = dbl_max_int_width/2;
00173         dbl_rext[i] = dbl_max_ext_width/2;
00174     }
00175 
00176     // External cladding (number 1) = hexagon
00177     G4Polyhedra* cladding1 = new G4Polyhedra("sCladding1", // Name
00178                         RotAnglePolygon,                   // Phi start
00179 //                         twopi*1/4.,                        // Phi start
00180 //                         0,                                 // Phi start
00181                         twopi*5/4.,                        // Phi total
00182                         int_Nsides,                        // Number of sides
00183                         int_NZplan,                        // Number of Z-planes
00184                         dbl_z_coord,                       // coordinates for Z-planes
00185                         dbl_rin,                           // coordinates for internal radius
00186                         dbl_rext);                         // coordinates for exernal  radius
00187     
00188     // Four concentric cylinders
00189     G4Tubs* CylindCladdingFuel[2]={0};
00190     G4UnionSolid* CladdingFuelUnion[2]={0};
00191     for(int i = 0; i<2; ++i) {
00192         dbl_CladRext = dbl_Clad2diam/2 - i*( dbl_CladPitch + dbl_CladThickness) - k * dbl_WallThickness;
00193         dbl_CladRint = dbl_CladRext - dbl_CladThickness + 2 * k * dbl_WallThickness;
00194         
00195         std::string str_CladName = std::string("s") + str_name[k].c_str() + std::to_string((int)(i+2));
00196         CylindCladdingFuel[i] = new G4Tubs(str_CladName.c_str(), dbl_CladRint, dbl_CladRext, 0.5*dbl_active_l[k], 0., twopi);
00197         
00198         if( i == 0) // Union of the first cylinder and the hexagon
00199             CladdingFuelUnion[i] = new G4UnionSolid((str_name[k] + "1+2").c_str(), cladding1, CylindCladdingFuel[i]);
00200         else {      // Union of the previous unions with the last cylinder
00201             std::string str_UnionName = str_name[k].c_str() + std::to_string((int)(i-1)) + std::string("+") + std::to_string((int)i);
00202             CladdingFuelUnion[i] = new G4UnionSolid(str_UnionName.c_str(), CladdingFuelUnion[i-1], CylindCladdingFuel[i]);
00203         }
00204     }
00205     
00206     if( k==0 ) {
00207         G4LogicCladdingUnion = new G4LogicalVolume(CladdingFuelUnion[1], G4CladdingMaterial,"lCladdingUnion");
00208         vec_LVname.push_back(G4LogicCladdingUnion->GetName());
00209         
00210         // Mother volume ( = entire rod) filled of water
00211         G4double r_water[2] = {0., 0.};
00212         G4double r_water_ext[2] = {dbl_rext[0]+p->dbl_rRodsGap/2., dbl_rext[0]+p->dbl_rRodsGap/2.};
00213         G4Polyhedra* WaterFuel  = new G4Polyhedra("sWaterFuel", RotAnglePolygon, twopi*5/4., int_Nsides, int_NZplan, dbl_z_coord, r_water, r_water_ext);
00214         G4LogicWaterFuelCladding = new G4LogicalVolume(WaterFuel, G4WaterFuelMaterial,"lWaterFuelCladding");
00215         vec_LVname.push_back(G4LogicWaterFuelCladding->GetName());
00216     }
00217     else if( k==1 ) {  // Fuel
00218         G4LogicFuel.clear();
00219         G4LogicFuelUnion   = new G4LogicalVolume(CladdingFuelUnion[1] , G4FuelMaterial ,"lFuelUnion");   // Union of fuel hexagon and cylinder required to calculate total fuel mass
00220         vec_LVname.push_back(G4LogicFuelUnion->GetName());
00221         G4LogicFuel.push_back( new G4LogicalVolume(cladding1            , G4FuelMaterial ,"lFuel" + std::to_string((int)G4LogicFuel.size()) ) ); // Hexagon
00222         vec_LVname.push_back(G4LogicFuel[G4LogicFuel.size()-1]->GetName());
00223         for(int i = 0; i<2; ++i) {
00224             G4LogicFuel.push_back( new G4LogicalVolume(CylindCladdingFuel[i] , G4FuelMaterial ,"lFuel" + std::to_string((int)G4LogicFuel.size()) ) ); // Inner cylinders
00225             vec_LVname.push_back(G4LogicFuel[G4LogicFuel.size()-1]->GetName());
00226         }
00227     }
00228   }
00229     
00230   delete [] dbl_z_coord;
00231   delete [] dbl_rin;
00232   delete [] dbl_rext;
00233 }
