00236 // Construction of the logical volumes for cladding and Fuel and water in the active area
00237 // 1 Hexagon + 2 concentric cylinders
00239 void TMXFuel::Build_IBR2M(TMXInputParameters* p,
00240                                   G4LogicalVolume* G4LogicCladdingUnion    , G4Material* G4CladdingMaterial,
00241                                   G4LogicalVolume* G4LogicWaterFuelCladding, G4Material* G4WaterFuelMaterial,
00242                                   G4LogicalVolume* G4LogicFuelUnion        , G4Material* G4FuelMaterial,
00243                                   std::vector<G4LogicalVolume*>& G4LogicFuel,
00244                                   std::vector<std::string>& vec_LVname )
00245 {
00246   G4double dbl_CladThickness =    0.45 *mm;  // Thickness of cladding wall
00247   G4double FuelRadius        = p->dbl_PolygoneWidth - dbl_CladThickness;  // Fuel radius
00248 
00249   // Four concentric cylinders
00250   G4Tubs* WaterS       = new G4Tubs("sWater"      , 0         , p->dbl_PolygoneWidth, p->CladdingZ, 0., twopi);
00251   G4Tubs* CladdingTubS = new G4Tubs("sCladdingTub", FuelRadius, p->dbl_PolygoneWidth, p->CladdingZ, 0., twopi);
00252   G4Tubs* FuelS        = new G4Tubs("sFuel"       , 0         , FuelRadius         , p->FuelZ, 0., twopi);
00253 
00254   G4LogicWaterFuelCladding = new G4LogicalVolume(WaterS      , G4WaterFuelMaterial,"lWaterFuelCladding");
00255   G4LogicCladdingUnion     = new G4LogicalVolume(CladdingTubS, G4CladdingMaterial ,"lCladding");        
00256   G4LogicFuelUnion         = new G4LogicalVolume(FuelS       , G4FuelMaterial  ,"lFuel");  
00257   G4LogicFuel.clear();
00258   G4LogicFuel.push_back( new G4LogicalVolume(FuelS , G4FuelMaterial ,"lFuel0" ));  
00259   vec_LVname.push_back(G4LogicCladdingUnion->GetName());
00260   vec_LVname.push_back(G4LogicWaterFuelCladding->GetName());
00261   vec_LVname.push_back(G4LogicFuelUnion->GetName());
00262   vec_LVname.push_back(G4LogicFuel[G4LogicFuel.size()-1]->GetName());
00263 }
