00026 TMXMCCubicVolume::TMXMCCubicVolume(TMXInputParameters* p) :
00027     m_p(p),
00028     fErrorLimit(100.),
00029     fCubicVolume_world(0.),
00030     fNavigator(0),
00031     fNbPoints(1),
00032     fNbPointsTotal(0),
00033     fIsSliceMode(false)
00034 {
