00044 
00045 void TMXMCCubicVolume::buildMapLogicalVolume() {
00046     fmap_LV_points.clear();
00047 
00048     G4LogicalVolumeStore *LVStore = G4LogicalVolumeStore::GetInstance();
00049     G4LogicalVolume *LV;
00050     for (G4LogicalVolumeStore::iterator it_LV = LVStore->begin(); it_LV != LVStore->end(); it_LV++) { // Read the logical volume store
00051         LV = *it_LV; // Define the logical volume
00052         if (LV->GetMaterial()) { // Check to not count the scoring worlds
00053             fmap_LV_points[LV->GetName()] = 0.;
00054         }
00055     }
00056 }
00057 
00058 void TMXMCCubicVolume::buildMapMaterial() {
00059     fmap_Material_points.clear();
00060 
00061     G4LogicalVolumeStore *LVStore = G4LogicalVolumeStore::GetInstance();
00062     G4Material *material;
00063     G4LogicalVolume *LV;
00064     for (G4LogicalVolumeStore::iterator it_LV = LVStore->begin(); it_LV != LVStore->end(); it_LV++) { // Read the logical volume store
00065         LV = *it_LV; // Define the logical volume
00066         material = LV->GetMaterial();
00067         if (material) {
00068             fmap_Material_points[material->GetName()] = 0.;
00069         }
00070     }
00071 }
