00107 // Gives the error from a Poissonnian distribution in percent
00108 double TMXMCCubicVolume::getError(const long nbPointsInsideVolume) const {
00109     return(100./std::sqrt((double)nbPointsInsideVolume));
00110 }
00111 
00112 double TMXMCCubicVolume::getMaximumError() const {
00113     double errorLV  = getMaximumErrorLV(),
00114            errorMat = getMaximumErrorMat();
00115     if (errorLV >= errorMat) {
00116         return(errorLV);
00117     }
00118     return(errorMat); // Error in percent
00119 }
00120 
00121 double TMXMCCubicVolume::getMaximumErrorLV() const {
00122     double error, maxError = 0.;
00123     for (auto it_LV = fmap_LV_points.begin(); it_LV != fmap_LV_points.end(); it_LV++) {
00124         error = getError(it_LV->second);
00125         if (!isinf(error) && maxError <= error) {
00126             maxError = error;
00127         }
00128     }
00129     return(maxError); // Error in percent
00130 }
00131 
00132 double TMXMCCubicVolume::getMaximumErrorMat() const {
00133     double error, maxError = 0.;
00134     for (auto it_LV = fmap_Material_points.begin(); it_LV != fmap_Material_points.end(); it_LV++) {
00135         error = getError(it_LV->second);
00136         if (!isinf(error) && maxError <= error) {
00137             maxError = error;
00138         }
00139     }
00140     return(maxError); // Error in percent
00141 }
00142 
00143 double TMXMCCubicVolume::getMaximumErrorSlicedLV() const {
00144     double error, maxError = 0.;
00145     for (int i = 0; i < fNbinsZ; i++) {
00146         error = getError(fSlicedVolumes[i]);
00147         if (!isinf(error) && maxError <= error) {
00148             maxError = error;
00149         }
00150     }
00151     return(maxError); // Error in percent
00152 }
