00155 // Return a random positon inside a box, parameters are half-length of the box
00156 G4ThreeVector TMXMCCubicVolume::getRandomPosition(const G4double halfX, const G4double halfY, const G4double halfZ) const {
00157     G4double randX = 10. * halfX * (2. * G4UniformRand() - 1.);
00158     G4double randY = 10. * halfY * (2. * G4UniformRand() - 1.); // or (double)(rand())/((double)(RAND_MAX))
00159     G4double randZ = 10. * halfZ * (2. * G4UniformRand() - 1.);
00160     return(G4ThreeVector(randX, randY, randZ));
00161 }
00162 
00163 G4ThreeVector TMXMCCubicVolume::getRandomPosition(const G4double halfX, const G4double halfY) const {
00164     G4double randX = 10. * halfX * (2. * G4UniformRand() - 1.);
00165     G4double randY = 10. * halfY * (2. * G4UniformRand() - 1.); // or (double)(rand())/((double)(RAND_MAX))
00166     G4double randZ = 10. * ((fMaxZ-fMinZ) * G4UniformRand() + fMinZ);
00167     return(G4ThreeVector(randX, randY, randZ));
00168 }
