00192 // Main method to manage the Monte Carlo calculations and satisfy error limits
00193 void TMXMCCubicVolume::runMCVolumeEstimator() {
00194     // Get world dimensions
00195     G4VSolid *solid = (*G4LogicalVolumeStore::GetInstance())[0]->GetSolid();
00196     const G4double halfX = ((G4Box*)solid)->GetXHalfLength() / cm,
00197                    halfY = ((G4Box*)solid)->GetYHalfLength() / cm,
00198                    halfZ = ((G4Box*)solid)->GetZHalfLength() / cm;
00199     fCubicVolume_world = 8. * halfX * halfY * halfZ;
00200 
00201     std::cout << "----------------------------------------------\n"
00202               << "| *****  Monte Carlo Volume Estimator  ***** |\n"
00203               << "----------------------------------------------\n"
00204               << "| * Number of points : " << fNbPoints << "\n"
00205               << "| * Target error     : " << fErrorLimit << " %\n"
00206               << "----------------------------------------------\n"
00207               << "| * Boundary of the world: \n"
00208               << "| \tX = [" << -halfX << " ; " << halfX << "] cm\n"
00209               << "| \tY = [" << -halfY << " ; " << halfY << "] cm\n"
00210               << "| \tZ = [" << -halfZ << " ; " << halfZ << "] cm\n"
00211               << "| * World Volume : " << fCubicVolume_world << " cm^3\n"
00212               << "| * Number of logical volumes : " << fmap_LV_points.size() << "\n"
00213               << "| * Number of materials       : " << fmap_Material_points.size() << "\n"
00214               << "----------------------------------------------\n"
00215               << std::endl;
00216 
00217     // First calculation with the number of points setup by the user
00218     const clock_t initialTime = clock();
00219     calculationMC(fNbPoints, halfX, halfY, halfZ);
00220     float time = ((float)(clock() - initialTime))/CLOCKS_PER_SEC;
00221 
00222     // Redo the calculation with the appropriate number of points to be lower than the error required by the user
00223     int countRun = 1;
00224     double maxError, FOM, overEstimatedTime;
00225     while (isLVorMatAboveErrorLimit()) {
00226         maxError = getMaximumError();
00227         FOM = 1/(maxError*maxError * time);
00228         overEstimatedTime = 1.1/(fErrorLimit * fErrorLimit * FOM);
00229         fNbPoints = (long)(overEstimatedTime/time * fNbPointsTotal) - fNbPointsTotal;
00230 
00231         if (m_p->Verbose >= 2) {
00232             printLogicalVolumeMap();
00233             printMaterialMap();
00234         }
00235 
00236         std::cout << "\n----------------------------------------------\n"
00237                   << "| Run " << countRun << " with " << fNbPointsTotal << " points\n"
00238                   << "----------------------------------------------\n"
00239                   << "| * Maximum error Logical Volume : " << getMaximumErrorLV()  << " %\n"
00240                   << "| * Maximum error Material       : " << getMaximumErrorMat() << " %\n"
00241                   << "| * Estimated FOM : " << FOM*10000.*60. << " /min\n"
00242                   << "| * Time for " << fNbPointsTotal << " points :\n"
00243                   << "| \t" << time << " s = " << time/60. << " min\n"
00244                   << "| * Estimated total time to be lower than " << fErrorLimit << " % :\n"
00245                   << "| \t" << overEstimatedTime << " s = " << overEstimatedTime/60. << " min\n"
00246                   << "| * Over Estimated additionnal time :\n"
00247                   << "| \t" << overEstimatedTime - time << " s = " << (overEstimatedTime - time)/60. << " min\n"
00248                   << "| * Additionnal number of points : " << fNbPoints << "\n"
00249                   << "----------------------------------------------\n"
00250                   << std::endl;
00251 
00252         calculationMC(fNbPoints, halfX, halfY, halfZ);
00253 
00254         time = ((float)(clock() - initialTime))/CLOCKS_PER_SEC;
00255         countRun++;
00256     }
00257 
00258     printLogicalVolumeMap();
00259     printMaterialMap();
00260 
00261     std::cout << "\n|---------------------------------------------\n"
00262               << "| Total Calculation time :\n"
00263               << "| \t" << time << " s = " << time/60. << " min\n"
00264               << "| Number of total points : " << fNbPointsTotal << "\n"
00265               << "| Maximum error on Logical Volume : " << getMaximumErrorLV()  << " %\n"
00266               << "| Maximum error on Material       : " << getMaximumErrorMat() << " %\n"
00267               << "|---------------------------------------------\n" << std::endl;
00268 }
