00270 void TMXMCCubicVolume::runSlicedLogicalVolumeMC() {
00271     // Get world dimensions
00272     const G4double halfX = std::max(m_p->maxX, std::abs(m_p->minX)) / cm, // ((G4Box*)solid)->GetXHalfLength() / cm,
00273                    halfY = std::max(m_p->maxY, std::abs(m_p->minY)) / cm, // ((G4Box*)solid)->GetYHalfLength() / cm,
00274                    lengthZ = (fMaxZ-fMinZ); // ((G4Box*)solid)->GetZHalfLength() / cm;
00275     fCubicVolume_world = 4. * halfX * halfY * lengthZ;
00276 
00277     std::cout << "----------------------------------------------\n"
00278               << "| *****  Monte Carlo Volume Estimator  ***** |\n"
00279               << "----------------------------------------------\n"
00280               << "| * Number of points : " << fNbPoints << "\n"
00281               << "| * Target error     : " << fErrorLimit << " %\n"
00282               << "----------------------------------------------\n"
00283               << "| * Boundary of the world: \n"
00284               << "| \tX = [" << -halfX << " ; " << halfX << "] cm\n"
00285               << "| \tY = [" << -halfY << " ; " << halfY << "] cm\n"
00286               << "| \tZ = [" <<  fMinZ << " ; " << fMaxZ << "] cm\n"
00287               << "| * World Volume : " << fCubicVolume_world << " cm^3\n"
00288               << "| * Logical Volume Sliced : " << fLogicalVolumeName << "\n"
00289               << "| * z = [" << fMinZ << ";" << fMaxZ << "] cm with " << fNbinsZ << " bins\n"
00290               << "----------------------------------------------\n"
00291               << std::endl;
00292 
00293     // First calculation with the number of points setup by the user
00294     const clock_t initialTime = clock();
00295     calculationMCSlicedLV(fNbPoints, halfX, halfY, fLogicalVolumeName);
00296     float time = ((float)(clock() - initialTime))/CLOCKS_PER_SEC;
00297 
00298     // Redo the calculation with the appropriate number of points to be lower than the error required by the user
00299     int countRun = 1;
00300     double maxError, FOM, overEstimatedTime;
00301     while (isSlicedLVAboveErrorLimit()) {
00302         maxError = getMaximumErrorSlicedLV();
00303         FOM = 1/(maxError*maxError * time);
00304         overEstimatedTime = 1.1/(fErrorLimit * fErrorLimit * FOM);
00305         fNbPoints = (long)(overEstimatedTime/time * fNbPointsTotal) - fNbPointsTotal;
00306 
00307         if (m_p->Verbose >= 2) {
00308             printSlicedVolume();
00309         }
00310 
00311         std::cout << "\n----------------------------------------------\n"
00312                   << "| Run " << countRun << " with " << fNbPointsTotal << " points\n"
00313                   << "----------------------------------------------\n"
00314                   << "| * Maximum error Logical Volume : " << getMaximumErrorSlicedLV() << " %\n"
00315                   << "| * Estimated FOM : " << FOM*10000.*60. << " /min\n"
00316                   << "| * Time for " << fNbPointsTotal << " points :\n"
00317                   << "| \t" << time << " s = " << time/60. << " min\n"
00318                   << "| * Estimated total time to be lower than " << fErrorLimit << " % :\n"
00319                   << "| \t" << overEstimatedTime << " s = " << overEstimatedTime/60. << " min\n"
00320                   << "| * Over Estimated additionnal time :\n"
00321                   << "| \t" << overEstimatedTime - time << " s = " << (overEstimatedTime - time)/60. << " min\n"
00322                   << "| * Additionnal number of points : " << fNbPoints << "\n"
00323                   << "----------------------------------------------\n"
00324                   << std::endl;
00325 
00326         calculationMCSlicedLV(fNbPoints, halfX, halfY, fLogicalVolumeName);
00327 
00328 
00329         time = ((float)(clock() - initialTime))/CLOCKS_PER_SEC;
00330         countRun++;
00331     }
00332 
00333     printSlicedVolume();
00334 
00335     std::cout << "\n|---------------------------------------------\n"
00336               << "| Total Calculation time :\n"
00337               << "| \t" << time << " s = " << time/60. << " min\n"
00338               << "| Number of total points : " << fNbPointsTotal << "\n"
00339               << "| Maximum error Logical Volume : " << getMaximumErrorSlicedLV() << " %\n"
00340               << "|---------------------------------------------\n" << std::endl;
00341 }
