00345 // Main method of Monte Carlo calculation
00346 void TMXMCCubicVolume::calculationMC(const long nbPoints, const G4double halfX, const G4double halfY, const G4double halfZ) {
00347     G4ThreeVector position;
00348     G4VPhysicalVolume *PV;
00349     G4LogicalVolume *LV;
00350     std::string materialName;
00351     for (long i=0; i<nbPoints; i++) {
00352         position = getRandomPosition(halfX, halfY, halfZ);
00353         PV = fNavigator->LocateGlobalPointAndSetup(position); // http://www.apc.univ-paris7.fr/~franco/g4doxy4.10/html/class_g4_navigator.html
00354         if (PV) {
00355             LV = PV->GetLogicalVolume();
00356             materialName = LV->GetMaterial()->GetName();
00357             fmap_LV_points[LV->GetName()]++;
00358             fmap_Material_points[materialName]++;
00359         }
00360     }
00361     fNbPointsTotal += nbPoints;
00362 }
00363 
00364 void TMXMCCubicVolume::calculationMCSlicedLV(const long nbPoints, const G4double halfX, const G4double halfY, G4String LVname) {
00365     G4ThreeVector position;
00366     G4VPhysicalVolume *PV;
00367     G4LogicalVolume *LV;
00368     int nz;
00369     for (long i=0; i<nbPoints; i++) {
00370         position = getRandomPosition(halfX, halfY);
00371         PV = fNavigator->LocateGlobalPointAndSetup(position); // http://www.apc.univ-paris7.fr/~franco/g4doxy4.10/html/class_g4_navigator.html
00372         if (PV) {
00373             LV = PV->GetLogicalVolume();
00374             if (LV->GetName() == LVname) {
00375                 nz = (int)(((position.z()/cm - fMinZ)/(fMaxZ - fMinZ)) * (double)fNbinsZ);
00376                 fSlicedVolumes[nz]++;
00377             }
00378         }
00379     }
00380     fNbPointsTotal += nbPoints;
00381 }
