00386 void TMXMCCubicVolume::printLogicalVolumeMap() const {
00387     // Sort alphabetically the map
00388     std::map<std::string, long> map_LV_points_ordered(fmap_LV_points.begin(), fmap_LV_points.end());
00389 
00390     int count = 0;
00391     int maxLengthName = 0, temp;
00392     double totalVolume = 0., volume;
00393     for (auto it_LV = map_LV_points_ordered.begin(); it_LV != map_LV_points_ordered.end(); it_LV++) {
00394         temp = it_LV->first.length();
00395         if (maxLengthName < temp) {
00396             maxLengthName = temp;
00397         }
00398     }
00399     std::cout << std::left
00400               << "|-" << std::string(maxLengthName, '-') << "---------------------------------------------------------------\n"
00401               << "| "
00402               << std::setw(maxLengthName) << "Logical Volume Name"
00403               << " | "
00404               << std::setw(12) << "Volume (cm3)"
00405               << " | "
00406               << std::setw(12) << "SigVol (cm3)"
00407               << " | "
00408               << std::setw(12) << "Error (%)"
00409               << " | "
00410               << std::setw(10) << "Total points\n"
00411               << "|-" << std::string(maxLengthName, '-') << "-|--------------|--------------|--------------|----------------\n";
00412 
00413     for (auto it_LV = map_LV_points_ordered.begin(); it_LV != map_LV_points_ordered.end(); it_LV++) {
00414         volume = it_LV->second/((double)fNbPointsTotal) * fCubicVolume_world;
00415         std::cout << std::left
00416                   << "| "
00417                   << std::setw(maxLengthName) << it_LV->first
00418                   << " | "
00419                   << std::setw(12) << volume
00420                   << " |  "
00421                   << std::setw(10) << volume * getError(it_LV->second) * 0.01
00422                   << " | "
00423                   << std::setw(10) << getError(it_LV->second)
00424                   << " % | "
00425                   << std::setw(10) << it_LV->second << " pts"
00426                   << std::endl;
00427         count++;
00428         totalVolume += volume;
00429     }
00430 
00431     // Make the full total calculation with errors
00432     std::cout << "|-" << std::string(maxLengthName, '-') << "---------------------------------------------------------------\n"
00433               << "| Number of logical volumes : " << count << "\n"
00434               << "| Total volume : " << totalVolume << " cm^3 (World Volume : " << fCubicVolume_world << " cm^3)\n"
00435               << "|-" << std::string(maxLengthName, '-') << "---------------------------------------------------------------\n"
00436               << std::endl;
00437 }
