00490 
00491 void TMXMCCubicVolume::printSlicedVolume() const {
00492     G4double lowerZ, upperZ, nz = (fMaxZ - fMinZ)/(double)fNbinsZ;
00493     G4double volume, totalVolume = 0.;
00494     long nPoints = 0;
00495     std::cout << std::left
00496               << "|-----------------------------------------------------------------------------\n"
00497               << "| "
00498               << std::setw(13) << "Interval (cm)"
00499               << " | "
00500               << std::setw(12) << "Volume (cm3)"
00501               << " | "
00502               << std::setw(12) << "SigVol (cm3)"
00503               << " | "
00504               << std::setw(12) << "Error (%)"
00505               << " | "
00506               << std::setw(10) << "Total points\n"
00507               << "|---------------|--------------|--------------|--------------|----------------\n";
00508 
00509     for (int i = 0; i < fNbinsZ; i++) {
00510         lowerZ = fMinZ  + nz * (double)i;
00511         upperZ = lowerZ + nz;
00512         volume = fSlicedVolumes[i]/(double)fNbPointsTotal * fCubicVolume_world;
00513         totalVolume += volume;
00514         nPoints += fSlicedVolumes[i];
00515         std::cout << std::left
00516                   << "| "
00517                   << "[" << std::right << std::setw(5) << lowerZ << " ; " << std::setw(5) << upperZ << "]"
00518                   << std::left
00519                   << " | "
00520                   << std::setw(12) << volume
00521                   << " |  "
00522                   << std::setw(10) << volume * getError(fSlicedVolumes[i]) * 0.01
00523                   << " | "
00524                   << std::setw(10) << getError(fSlicedVolumes[i]) << " %"
00525                   << " | "
00526                   << std::setw(10) << fSlicedVolumes[i] << " pts"
00527                   << std::endl;
00528     }
00529     std::cout << "|--------------------------------------------------------------------------\n"
00530               << "| Total Points inside " << fLogicalVolumeName << " : " << nPoints << '\n'
00531               << "| Total Volume : " << totalVolume << "  " << totalVolume*getError(nPoints)*0.01 << " cm3\n"
00532               << "|--------------------------------------------------------------------------\n";
00533 }
