00019 TH2D* GetAndDraw2D_histo(TCanvas* canvas, std::string str_CanvaName, std::string str_histoName, std::string Xlabel, std::string Ylabel, std::string Zlabel, double scaling_factor, std::string drawing_Option, bool save_root, bool save_png) {
00020     canvas = new TCanvas(str_CanvaName.c_str(),str_CanvaName.c_str());
00021     TH2D* histo_2D = (TH2D*)gDirectory->Get(str_histoName.c_str());
00022     histo_2D->SetNameTitle(str_CanvaName.c_str(), "");
00023     histo_2D->SetContour(100);
00024     histo_2D->GetXaxis()->SetTitle(Xlabel.c_str());
00025     histo_2D->GetYaxis()->SetTitle(Ylabel.c_str());
00026     histo_2D->GetZaxis()->SetTitle(Zlabel.c_str());
00027     histo_2D->GetZaxis()->SetTitleOffset(1.3);
00028     histo_2D->Scale(scaling_factor);
00029     histo_2D->SetContour(100);
00030     canvas->SetLogz();
00031     histo_2D->Draw(drawing_Option.c_str());
00032     if(save_root) { canvas->SaveAs((str_CanvaName + ".root").c_str());}
00033     if(save_png)  { canvas->SaveAs((str_CanvaName + ".png" ).c_str());} 
00034     
00035     return histo_2D;
00036 }
