00052 TH2D* GetAndDraw2D_histoFromTree(TCanvas* canvas, std::string name_tree, std::string str_CanvaName, std::string str_histoName, 
00053                                  std::string Xlabel, std::string Xvariable, int xbin, double Xmin, double Xmax, 
00054                                  std::string Ylabel, std::string Yvariable, int ybin, double Ymin, double Ymax, 
00055                                  std::string Zlabel, double scaling_factor, std::string drawing_Option, std::string str_cut, 
00056                                  bool save_root, bool save_png) {
00057     canvas = new TCanvas(str_CanvaName.c_str(),str_CanvaName.c_str());
00058     TTree* tree = (TTree*) gDirectory->GetObjectChecked((name_tree.c_str()), "TTree");
00059     std::string str_buildHisto = Xvariable + ":" + Yvariable + " >> " + str_histoName + "(" 
00060                                 + std::to_string(xbin) + ", " + std::to_string(Xmin) + ", " + std::to_string(Xmax) + ", " 
00061                                 + std::to_string(ybin) + ", " + std::to_string(Ymin) + ", " + std::to_string(Ymax) + ")";
00062     tree->Draw(str_buildHisto.c_str(), str_cut.c_str(), drawing_Option.c_str());
00063     TH2D* histo_2D = (TH2D*)gDirectory->Get(str_histoName.c_str());
00064     histo_2D->SetNameTitle(str_CanvaName.c_str(), "");
00065     histo_2D->SetContour(100);
00066     histo_2D->GetXaxis()->SetTitle(Xlabel.c_str());
00067     histo_2D->GetYaxis()->SetTitle(Ylabel.c_str());
00068     histo_2D->GetZaxis()->SetTitle(Zlabel.c_str());
00069     histo_2D->GetZaxis()->SetTitleOffset(1.3);
00070     histo_2D->Scale(scaling_factor);
00071     histo_2D->SetContour(100);
00072     canvas->SetLogz();
00073     histo_2D->Draw(drawing_Option.c_str());
00074     if(save_root) { canvas->SaveAs((str_CanvaName + ".root").c_str());}
00075     if(save_png)  { canvas->SaveAs((str_CanvaName + ".png" ).c_str());} 
00076     
00077     return histo_2D;
00078 }
