00088   
00089   // Reading name of the ROOT file
00090   size_t last_slash = inputFile.rfind("/");
00091   if(last_slash == std::string::npos) { last_slash = 0;}
00092   std::string FileName = inputFile.substr(last_slash, inputFile.rfind(".root")-last_slash);
00093   TFile *f_ROOT = new TFile( (inputFile).c_str() );    
00094 
00095   // Number of primary particles (Nparticles)
00096   int Nparticles = 50000;
00097   if(false) { // Nparticles coming from the scoring of primary protons
00098        TH2D* MappingYZ_pSource = (TH2D*)gDirectory->Get("MappingYZ_pSrc");
00099        if(MappingYZ_pSource->Integral() !=0) {Nparticles = MappingYZ_pSource->Integral();}
00100        std::cout << "No proton source : " << Nparticles <<std::endl;
00101   }
00102   
00103   // To save histograms in png or root files
00104   bool save_root = false;
00105   bool save_png  = false;
00106   
00107   // In case of an ADS
00108   double Beam_Power  = 0.05; // in MW
00109   double Beam_Energy = 70; // in MeV
00110   std::string str_beamPower = "50 kW_{beam}";
00111   double ADS_gain=1;
00112   
00113   // In case of a critical reactor
00114   bool bool_critical_reactor = true;
00115   double core_PowerTh = 10; // MW
00116   double M_qe = 1.602176634E-19;
00117   double nu_tot = 2.451228;
00118   double k_eff = 1;
00119   if(bool_critical_reactor) {str_beamPower = "10 MW_{th}";}
00120   
00121   double scaling_factor = 1;
00122   double scaling_rawData = 1;
00123   double rescaling_Data = 1;
00124 
00125   if(f_ROOT)
00126   {
00127     double Gain_error = 0;
00128     double Pth_error = 0;
00129     if(true) {
00130         TH1D* Mapping_Edep = (TH1D*)gDirectory->Get("MappingEdep"); // Histogram of full energy deposition
00131         double error = 0; // To calculate the error with the sum of squares
00132         double integral = Mapping_Edep->IntegralAndError(0, (Mapping_Edep->GetNbinsX() + 1), error, "");
00133         if(!bool_critical_reactor) {
00134             std::cout << "Total thermal power : " << integral*Beam_Power/(double)(Beam_Energy*Nparticles)/1000. << ":±:" << Pth_error*Beam_Power/(double)(Beam_Energy*Nparticles)/1000. << ": kW @ " +str_beamPower <<std::endl;
00135             ADS_gain = integral/(double)(Beam_Energy*1E6*Nparticles)-1;
00136             Pth_error = TMath::Sqrt( error/integral * error/integral + 1/Nparticles)*integral;
00137             Gain_error = ADS_gain * Pth_error/integral;
00138             std::cout << "ADS gain G = " << ADS_gain << ":±:" << Gain_error << std::endl;
00139         }
00140         else {
00141             double EperFission = nu_tot * integral/(double)Nparticles;
00142             scaling_factor = core_PowerTh*1E6 / (integral * M_qe);
00143             std::cout << "E per Fission : " << EperFission*1E-6 << " MeV/fission" << std::endl;
00144             std::cout << "N fission per second : " << Nparticles * scaling_factor/ nu_tot << std::endl;
00145             std::cout << "N neutron per second : " << Nparticles * scaling_factor << std::endl;
00146             std::cout << "Scaling factor : " << scaling_factor << std::endl;
00147 //             std::cout << "correct : " << nu_tot*core_PowerTh/(EperFission*1E-6)/Nparticles << std::endl;
00148         }
00149     }
00150 
00151     // Scaling factors
00152     if(bool_critical_reactor) {
00153         scaling_rawData = scaling_factor;   // scaling of raw data (ntuples of flux energy distributions)
00154         rescaling_Data  = scaling_factor; // rescaling of scoring mesh
00155     }
00156     else {
00157         scaling_rawData = Beam_Power/(double)(Beam_Energy*M_qe*Nparticles); // Number of source particles per second -> scaling of raw data (ntuples of flux energy distributions)
00158         rescaling_Data = Beam_Power/(double)(Beam_Energy*M_qe*Nparticles); // Data already scaled to 1 MW_beam (Scoring mesh)
