01901             for(int j = 1; j < int_ybin; ++j) {
01902                 double Content = MappingAZposFission->GetBinContent(i,j);
01903                 double error = std::sqrt(MappingAZposFission->GetBinContent(i,j)); // Get the square root of the bin content
01904                 if(bool_critical_reactor) { error = error*std::sqrt(scaling_rawData); } // Renormalize by the square root of the scaling factor
01905                 else { error = error /std::sqrt(ADS_gain*Beam_Energy*1.602E-19*Nparticles);}
01906                 
01907                 if(Content != 0) {
01908                     if(bool_critical_reactor) { 
01909                         double conversion_GWh = 1E3 * 3600. / core_PowerTh;
01910                         double mass_fission = TMath::Nint(MappingAZposFission->GetYaxis()->GetBinCenter(j)) * Content / TMath::Na();
01911                         std::cout << "(" << TMath::Nint(MappingAZposFission->GetXaxis()->GetBinLowEdge(i)) << ", " << TMath::Nint(MappingAZposFission->GetYaxis()->GetBinCenter(j)) 
01912                                   << ") : " << 100 * conversion_GWh * mass_fission/mass_tot << " +- " << 100 * conversion_GWh * TMath::Nint(MappingAZposFission->GetYaxis()->GetBinCenter(j)) * error / TMath::Na()/mass_tot << str_unit << std::endl;            
