02078                 if ( NPosX*NPosX + NPosY*NPosY > 100*100 && NPosX*NPosX + NPosY*NPosY < 200*200 ) { 
02079                     FissionSpectrum->Fill(energyK); 
02080                 }
02081                 FissionVolume->Fill(FissionVol);
02082             }
