00061 void DefineMaterials::Initialize(TMXInputParameters* parameter) {
00062     fparameter = parameter;
00063     
00065     // TMX Materials
00066     
00067     // World materials
00068     fUniverseMaterial = nullptr;
00069     fAirMaterial = nullptr;
00070     fVacuum = nullptr;
00071     fConcreteMaterial = nullptr;
00072     fWorldLeadMaterial = nullptr;
00073     fWorldWaterMaterial = nullptr;
00074     fWorldAluMaterial = nullptr;
00075     fWorldBiMaterial = nullptr;
00076     fWorldAlF3Material = nullptr;
00077     
00078     // Core materials
00079     fCladAluMaterial = nullptr;
00080     fAlAlloyMaterial = nullptr;
00081     fFuelWaterMaterial = nullptr;
00082     fFuelAbsorMaterial = nullptr;
00083     fReactorMaterial = nullptr;
00084     fLeadRigMaterial = nullptr;
00085     fCladSsMaterial = nullptr;
00086     fFuelCladAluMaterial = nullptr;
00087     fFuelCladAluTempFuelMaterial = nullptr;
00088     fFuelReflMaterial4.clear();
00089     
00090     // Nuclear fuel
00091     fUO2AlMaterial = nullptr;
00092     fThO2_Material = nullptr;
00093     fRecycledPu_Material = nullptr;
00094     fFreshMOX_Material = nullptr;
00095     fAm241_Material = nullptr;
00096     fAm242_Material = nullptr;
00097     fAm243_Material = nullptr;
00098     fPu238_Material = nullptr;
00099     fPu241_Material = nullptr;
00100     fMixAm_Material = nullptr;
00101     fMixCm_Material = nullptr;
00102     fDirtPu_Material = nullptr;
00103     fThPuO2_Material = nullptr;
00104     fFuelBurnupMWd_kgU.clear();
00105     
00106     // c ========  Definition of cell in LVR-15 model  =================
00107     // c
00108     // c FA temperature                  - 376 K    = 3.239992e-8 MeV
00109     // c FA surface temperature          - 353 K    = 8.617e-11*353 = 3.041801e-8 MeV
00110     // c Water temperature inside FA     - 318 K    = 2.740206e-8 MeV
00111     // c Water temperature inside LVR-15 - 293 K    = 2.524781e-8 MeV
00112 
00113   ftemperature_world      = fparameter->TemperatureWorld;
00114   ftemperature_fuel       = 376*kelvin; // UO2 temperature
00115   ftemperature_FuelSurf   = 353*kelvin; // Fuel Cladding
00116   ftemperature_PrimaryCoolantInCore  = 318*kelvin; // Temperature of water insidde FA + temperature non-FA elements in core
00117   ftemperature_PrimaryCoolantOutOfCore = 293*kelvin; // Temperature of water inside LVR-15 reactor
00118   ftemperature_PrimaryCoolantAndReflectorAndCR = ftemperature_PrimaryCoolantInCore; //350*kelvin; // Temperature of water inside fuel assembly, control rods, beryllium rods
00119 
00120 //   ftemperature_fuel       = 293*kelvin; // UO2 temperature
00121 //   ftemperature_FuelSurf   = 293*kelvin; // Fuel Cladding
00122 //   ftemperature_PrimaryCoolantInCore  = 293*kelvin; // Temperature of water insidde FA + temperature non-FA elements in core
00123 //   ftemperature_PrimaryCoolantOutOfCore = 293*kelvin; // Temperature of water inside LVR-15 reactor
00124 //   ftemperature_PrimaryCoolantAndReflectorAndCR = 293*kelvin; // Temperature of water inside fuel assembly, control rods, beryllium rods
00125 }
