00130 // Read MCNP material card file from CVR to build GEANT4 material with same fuel compositions
00131 //
00135 std::vector< std::vector <TMXMaterial*>>* DefineMaterials::GetFuelBurnupMWd_kgU(double burnup_MWd_kgU) {
00136     std::vector<std::pair<int, G4double> > V_nAtoms; // vector of atomic fractions
00137     bool bool_atomic_fraction = false;
00138   
00139 //     std::string strFileName = "../../LVR15fuel_with_burnup";
00140     std::string strFileName = fparameter->PathToBurnupFile;
00141     std::ifstream myfile;
00142     myfile.open (strFileName, std::ifstream::binary);
00143     std::string strLine;
00144     
00145     // Looking for the position of the closest burnup in the data file
00146     std::size_t Burnup_Pos=0;
00147     double min_difference = burnup_MWd_kgU + 10;
00148     std::string Infile_burnup = "";
00149     bool new_format = false;
00150     
00151     while(std::getline (myfile,strLine)) {
00152         std::size_t Pos = strLine.find("Burnup", 0);
00153         std::size_t file_pos = myfile.tellg();
00154         if(Pos != std::string::npos) {
00155 //             std::cout << strLine.substr(Pos+7, strLine.size() -15 - Pos) << std::endl;
00156 //             std::cout << "min_difference : " << min_difference << std::endl;
00157 //             std::cout << "difference : " << std::abs(burnup_MWd_kgU - std::stod(strLine.substr(Pos+7, strLine.size() -15 - Pos))) << std::endl;
00158             if( std::abs(burnup_MWd_kgU - std::stod(strLine.substr(Pos+7, strLine.size() -15 - Pos))) <= min_difference) {
00159                 min_difference = std::abs(burnup_MWd_kgU - std::stod(strLine.substr(Pos+7, strLine.size() -15 - Pos)));
00160                 Burnup_Pos = file_pos;   // Position in the file of the closest burnup to burnup_MWd_kgU
00161                 Infile_burnup = strLine.substr(Pos+7, strLine.size() -15 - Pos);
00162                 new_format = false;
00163             }
00164         }
00165         else if(strLine.find("vyhoreni", 0) != std::string::npos) { // Implementation of a new format of MCNP material file; "vyhoreni" means "burnup" in Czech
00166             std::string mat_name;
00167             std::getline (myfile,mat_name); // Get the following line to read the material name since each vertical slice has its own burnup
00168             if( mat_name.at(5) == '3' ) { // First vertical slice
00169                 Pos = strLine.find("vyhoreni", 0);
00170                 if( std::abs(burnup_MWd_kgU - std::stod(strLine.substr(Pos+8, strLine.size() -15 - Pos-8))) <= min_difference) {
00171                     min_difference = std::abs(burnup_MWd_kgU - std::stod(strLine.substr(Pos+8, strLine.size() -15 - Pos-8)));
00172                     Burnup_Pos = file_pos;   // Position in the file of the closest burnup to burnup_MWd_kgU
00173                     Infile_burnup = strLine.substr(Pos+8, strLine.size() -15 - Pos-8);
00174                     new_format = true;
00175                 }
00176             }
00177         }
00178     }
00179     
00180     int length_mat = 7;
00181     if(new_format) {length_mat =6;}
00182     
00183     // Check if the fuel with this burnup already exists
00184     if (fFuelBurnupMWd_kgU.find(Infile_burnup) != fFuelBurnupMWd_kgU.end()) {
00185         return &(fFuelBurnupMWd_kgU[Infile_burnup]);
00186     }
00187     else {
00188         // Come back to the position of the closest burnup
00189         myfile.clear();
00190         myfile.seekg (Burnup_Pos, myfile.beg);
00191 //         while(std::getline (myfile,strLine) && strLine.front() != 'c') { // Does not work properly if one isotope is commented
00192         while(std::getline (myfile,strLine)  // We read the file until we can't
00193             && ( new_format                  // and with the new format 
00194               || (strLine.size() > 12        // or with the first format we do not reach the next fuel element
00195                 && strLine.substr(0,12) != "c ----- Fuel")
00196               || strLine.size() <= 12)
00197             ) {
00198             
00199             
00200             if(  strLine.front() == 'm' 
00201             && ((strLine.at(strLine.size()-2) == '1' && !new_format)
00202                 || (strLine.at(5) == '3' && new_format))              
00203             ) { // if new material ending by 1 => new layer or 3 with the new format
00204                 if(bool_atomic_fraction) {  // If element were given with atom fraction, add the elements by mass fraction before adding a new material
00205                     AddAtomicFractions( fFuelBurnupMWd_kgU[Infile_burnup].back().back() , &V_nAtoms);
00206                     bool_atomic_fraction = false;
00207                 }
00208                 fFuelBurnupMWd_kgU[Infile_burnup].push_back( { new TMXMaterial( ("fuel_UO2Al_" + strLine.substr(0,length_mat)), 
00209                                                             4.71983*(g/cm3), 
00210                                                             GetNumberOfElements(myfile, myfile.tellg(), new_format), 
00211                                                             kStateSolid, 
00212                                                             ftemperature_fuel) } );
00213                 
00214                 if(new_format) {
00215                     AddElementByMassOrAtom( strLine, bool_atomic_fraction, fFuelBurnupMWd_kgU[Infile_burnup].back().back(), &V_nAtoms);
00216                 }
00217             }
00218             else if (strLine.front() == 'm' ||
00219                 (new_format                  // with the new format 
00220                 && strLine.size() > 12      
00221                 && strLine.substr(0,12) == "c ----- Fuel")
00222             ) { // if new material not ending by 1 => new slice in the horizontal plane
00223                 
00224                 if(new_format                  // with the new format 
00225                 && strLine.size() > 12      
00226                 && strLine.substr(0,12) == "c ----- Fuel") {
00227                     std::getline (myfile,strLine);
00228                     std::getline (myfile,strLine);
00229                 }
00230                 
00231                 if(strLine.at(5) == '3' && new_format) { break;}
00232                 
00233                 if(bool_atomic_fraction) { 
00234                     AddAtomicFractions( fFuelBurnupMWd_kgU[Infile_burnup].back().back() , &V_nAtoms);
00235                     bool_atomic_fraction = false;
00236                 }
00237                 
00238 //                 fFuelBurnupMWd_kgU[Infile_burnup].back().push_back( new TMXMaterial( ("fuel_UO2Al_" + strLine).c_str(), 
00239                 fFuelBurnupMWd_kgU[Infile_burnup].back().push_back( new TMXMaterial( ("fuel_UO2Al_" + strLine.substr(0,length_mat)), 
00240                                                                 4.71983*(g/cm3), 
00241                                                                 GetNumberOfElements(myfile, myfile.tellg(), new_format), 
00242                                                                 kStateSolid, 
00243                                                                 ftemperature_fuel ) );
00244                 if(new_format) {
00245                     AddElementByMassOrAtom( strLine, bool_atomic_fraction, fFuelBurnupMWd_kgU[Infile_burnup].back().back(), &V_nAtoms);
00246                 }
00247             }
00248             else { // New elements in the layer x and the slice y
00249                 AddElementByMassOrAtom( strLine, bool_atomic_fraction, fFuelBurnupMWd_kgU[Infile_burnup].back().back(), &V_nAtoms);
00250             }
00251         }
00252         
00253         if(bool_atomic_fraction) {  // If element were given with atom fraction, add the elements by mass fraction before adding a new material
00254             AddAtomicFractions( fFuelBurnupMWd_kgU[Infile_burnup].back().back() , &V_nAtoms);
00255             bool_atomic_fraction = false;
00256         }
00257         
00258         myfile.close();    
00259         
00260         return &(fFuelBurnupMWd_kgU[Infile_burnup]);
00261     }
00262 }
00263 
00264 int DefineMaterials::GetNumberOfElements(std::ifstream& myfile, size_t pos_begin, bool new_format) {
00265     int Nelem = 0; // Counter of elements ( = lines)
00266     std::string strLine;
00267     
00268     myfile.seekg (pos_begin, myfile.beg); // Go to the start position
00269     while(std::getline (myfile,strLine)  // We read the file until we can't or we reach the next layer
00270            && strLine.size() > 12 
00271            && strLine.substr(0,12) != "c ----- Fuel"
00272            && strLine.front() != 'm') // Look for a new material
00273         ++Nelem;
00274     if(new_format)  ++Nelem;
00275     myfile.clear();
00276     myfile.seekg (pos_begin, myfile.beg); // go back to the start position
00277     
00278     return Nelem;
00279 }
00280 
00281 void DefineMaterials::AddElementByMassOrAtom( std::string strLine, bool &bool_atomic_fraction, TMXMaterial* mat, std::vector<std::pair<int, G4double>>* V_nAtoms) {
00282     std::string IZA = strLine.substr(strLine.find(".", 0)-5, 5) + "0"; // Extract IZA number
00283     double Fraction_value = std::stod( strLine.substr(strLine.find(".", 0)+4, strLine.size() - 4 - strLine.find(".", 0)) ); // Atomic or mass fraction
00284     
00285     if(Fraction_value < 0) { // If mass fraction
00286         mat->AddElement(std::atoi(IZA.c_str()), Fraction_value);}
00287     else { // if atomic fraction => the mass fraction is calculated before adding a new material
00288         bool_atomic_fraction = true;
00289         V_nAtoms->push_back( {std::atoi(IZA.c_str()) , Fraction_value} );
00290     }    
00291 }
