00304 void DefineMaterials::DefineUniverseMaterial()
00305 {
00307     // Universe Air (Vacuum)
00309     fUniverseMaterial = TMXMaterial::Find("universe_world_air");
00310     if( !fUniverseMaterial ) {
00311         fUniverseMaterial = new TMXMaterial("universe_world_air",universe_mean_density*g/cm3, 1, kStateGas, ftemperature_world, 3.e-18*hep_pascal);
00312         fUniverseMaterial->AddElement(10010,1);
00313     }
00314 }
