00316 void DefineMaterials::DefineAirMaterial()
00317 {
00319   // Air
00321 // c -----  Air, 0.001293  ----------
00322 // m5      7014.70c    -0.75      $ N
00323 //         8016.70c    -0.25      $ O
00324 // c
00325   
00326     fAirMaterial = new TMXMaterial("world_air_mat", 0.001293*(g/cm3), 2, kStateGas, ftemperature_PrimaryCoolantOutOfCore);
00327     fAirMaterial->AddElement(70140,0.75);
00328     fAirMaterial->AddElement(80160,0.25);
00329 }
