00515 void DefineMaterials::DefineFuelAbsorMaterial()
00516 {
00518   // B4C
00520 
00521     // second version of MCNP model
00522 // c ----- B4C (100hm.% B10), 2.530 g/cm3
00523 // m7     5010.70c 4   $    B10
00524 //        6000.70c 1   $    C
00525   G4int nAtoms;
00526   fFuelAbsorMaterial = new TMXMaterial("fuel_B4C",2.53*(g/cm3), 2, kStateSolid, ftemperature_PrimaryCoolantInCore);
00527   fFuelAbsorMaterial->AddElement( 50100, nAtoms = 4);
00528   fFuelAbsorMaterial->AddElement( 60120, nAtoms = 1);
00529 
00530 //   // First version of MCNP model sent by CVR
00531   // commented on 17/06/2021
00532 // // c ----- B4C (nat. 19.9 izot. % B10), 1.1 g/cm3
00533 // // m700   5010.70c 19.9   $    B10
00534 // //        5011.70c 80.1   $    B11 
00535 // //        6000.70c 25   $    C
00536 //   G4int nAtoms;
00537 //   fFuelAbsorMaterial = new TMXMaterial("fuel_B4C",1.1*(g/cm3), 3, kStateSolid, ftemperature_PrimaryCoolantInCore);
00538 //   fFuelAbsorMaterial->AddElement( 50100, nAtoms = 199);
00539 //   fFuelAbsorMaterial->AddElement( 50110, nAtoms = 801);
00540 // //   fFuelAbsorMaterial->AddElement(G4NistManager::Instance()->FindOrBuildElement( "C" ), nAtoms = 250);
00541 //   fFuelAbsorMaterial->AddElement( 60120, nAtoms = 250);
