00544 void DefineMaterials::DefineReactorMaterial()
00545 {
00547     // Reactor vessel material
00549 
00550     // c -----  Reactor vessel, stainless steel, 7.560663 g/cm3  -------
00551     // m6     6000.70c  -0.0012             $ C
00552     //       14028.70c  -0.009223           $ Si
00553     //       14029.70c  -0.000467
00554     //       14030.70c  -0.00031
00555     //       15031.70c  -0.00035            $ P
00556     //       16032.70c  -0.00033257         $ S
00557     //       16033.70c  -0.000002625
00558     //       16034.70c  -0.000014735
00559     //       16036.70c  -0.00000007
00560     //       22046.70c  -0.00036            $ Ti           
00561     //       22047.70c  -0.0003285
00562     //       22048.70c  -0.003321
00563     //       22049.70c  -0.0002475
00564     //       22050.70c  -0.000243
00565     //       24050.70c  -0.008038           $ Cr
00566     //       24052.70c  -0.155010
00567     //       24053.70c  -0.017577
00568     //       24054.70c  -0.004375
00569     //       25055.70c  -0.02               $ Mn
00570     //       26054.70c  -0.039649           $ Fe
00571     //       26056.70c  -0.626998
00572     //       26057.70c  -0.015039
00573     //       26058.70c  -0.001914
00574     //       28058.70c  -0.064673           $ Ni
00575     //       28060.70c  -0.024912
00576     //       28061.70c  -0.001083
00577     //       28062.70c  -0.003452
00578     //       28064.70c  -0.000880
00579     
00580     fReactorMaterial = new TMXMaterial("fuel_vessel_mat", 7.560663*(g/cm3), 28, kStateSolid, ftemperature_PrimaryCoolantOutOfCore);
00581     //   fReactorMaterial->AddElement(C_12,0.0012);
00582     // --->  Isotope:   C12   Z =  6   N =  12   A =  12.00 g/mole   abundance: 98.930 %
00583     // --->  Isotope:   C13   Z =  6   N =  13   A =  13.00 g/mole   abundance:  1.070 %
00584 //     fReactorMaterial->AddElement(G4NistManager::Instance()->FindOrBuildElement( "C" ),0.0012);
00585     fReactorMaterial->AddElement( 60120,0.0012);      // C-12
00586     fReactorMaterial->AddElement(140280,0.009223);    // Si28
00587     fReactorMaterial->AddElement(140290,0.000467);    // Si29
00588     fReactorMaterial->AddElement(140300,0.00031);     // Si30
00589     fReactorMaterial->AddElement(150310,0.00035);     // P_31
00590     fReactorMaterial->AddElement(160320,0.00033257);  // S_32
00591     fReactorMaterial->AddElement(160330,0.000002625); // S_33
00592     fReactorMaterial->AddElement(160340,0.000014735); // S_34
00593     fReactorMaterial->AddElement(160360,0.00000007);  // S_36
00594     fReactorMaterial->AddElement(220460,0.00036);     // Ti46
00595     fReactorMaterial->AddElement(220470,0.0003285);   // Ti47
00596     fReactorMaterial->AddElement(220480,0.003321);    // Ti48
00597     fReactorMaterial->AddElement(220490,0.0002475);   // Ti49
00598     fReactorMaterial->AddElement(220500,0.000243);    // Ti50
00599     fReactorMaterial->AddElement(240500,0.008038);    // Cr50
00600     fReactorMaterial->AddElement(240520,0.155010);    // Cr52
00601     fReactorMaterial->AddElement(240530,0.017577);    // Cr53
00602     fReactorMaterial->AddElement(240540,0.004375);    // Cr54
00603     fReactorMaterial->AddElement(250550,0.02);        // Mn55
00604     fReactorMaterial->AddElement(260540,0.039649);    // Fe54
00605     fReactorMaterial->AddElement(260560,0.626998);    // Fe56
00606     fReactorMaterial->AddElement(260570,0.015039);    // Fe57
00607     fReactorMaterial->AddElement(260580,0.001914);    // Fe58
00608     fReactorMaterial->AddElement(280580,0.064673);    // Ni58
00609     fReactorMaterial->AddElement(280600,0.024912);    // Ni60
00610     fReactorMaterial->AddElement(280610,0.001083);    // Ni61
00611     fReactorMaterial->AddElement(280620,0.003452);    // Ni62
00612     fReactorMaterial->AddElement(280640,0.000880);    // Ni64
00613 }
