00623 void DefineMaterials::DefineFuelReflMaterial4()
00624 {
00626     // Berylium
00628     // c ----- Be, 1.85 g/cm3
00629     // m4      1001.70c 8.565E-05
00630     //       1002.70c 1.545E-07
00631     //       1003.70c 1.659E-04
00632     //       2003.70c 5.109E-06
00633     //       2004.70c 8.891E-04
00634     //       3006.70c 1.204E-05
00635     //       3007.70c 1.374E-07
00636     //       4009.70c 9.988E-01
00637     //       5010.70c 3.795E-11
00638     //       5011.70c 1.185E-09
00639     //       6012     3.209E-14
00640     //       6013     3.838E-19
00641     // mt4    be.10t              $ 294 K
00642     //   G4Material* fuel_Berylium_m4 = new G4Material("fuel_Berylium_m4",1.85*(g/cm3), 12, kStateSolid, ftemperature_PrimaryCoolantInCore);
00643     fFuelReflMaterial4.push_back( new TMXMaterial("fuel_Berylium_m4",1.85*(g/cm3), 12, kStateSolid, ftemperature_PrimaryCoolantAndReflectorAndCR));
00644     
00645     // Atomic fraction
00646     std::vector<std::pair<int, G4double> > V_nAtoms4 { {10010,8.565E-05}, // 1001
00647     {10020, 1.545E-07}, // 1002
00648     {10030, 1.659E-04}, // 1003
00649     {20030, 5.109E-06}, // 2003
00650     {20040, 8.891E-04}, // 2004
00651     {30060, 1.204E-05}, // 3006
00652     {30070, 1.374E-07}, // 3007
00653     {40090, 9.988E-01}, // 4009
00654     {50100, 3.795E-11}, // 5010
00655     {50110, 1.185E-09}, // 5011
00656     {60120, 3.209E-14}, // 6012
00657     {60130, 3.838E-19}};// 6013
00658 
00659     G4double M_tot = 0; // Total mass * Na
00660     for(size_t i = 0; i< V_nAtoms4.size(); ++i) {
00661         M_tot += V_nAtoms4[i].second * TMXElementStore::GetElement(V_nAtoms4[i].first)->Mweight()*(g/mole);
00662     }
00663 
00664     for(size_t i = 0; i< V_nAtoms4.size(); ++i) {
00665         if(V_nAtoms4[i].first == 40090) { fFuelReflMaterial4.back()->AddElement(V_nAtoms4[i].first, V_nAtoms4[i].second * TMXElementStore::GetElement(V_nAtoms4[i].first)->Mweight()*(g/mole) / M_tot, "TS_Beryllium_Metal"); }
00666         else { fFuelReflMaterial4.back()->AddElement(V_nAtoms4[i].first, V_nAtoms4[i].second * TMXElementStore::GetElement(V_nAtoms4[i].first)->Mweight()*(g/mole) / M_tot); }
00667     }
00668 
00669     // m41      1001.70c 1.346E-04
00670     //       1002.70c 3.303E-07
00671     //       1003.70c 2.563E-04
00672     //       2003.70c 7.297E-06
00673     //       2004.70c 1.347E-03
00674     //       3006.70c 1.337E-05
00675     //       3007.70c 2.125E-07
00676     //       4009.70c 9.982E-01
00677     //       5010.70c 4.593E-11
00678     //       5011.70c 2.232E-09
00679     //       6012     8.245E-14
00680     //       6013     1.339E-18
00681     // mt41    be.10t              $ 294 K
00682     //   G4Material* fuel_Berylium_m41 = new G4Material("fuel_Berylium_m41",1.85*(g/cm3), 12, kStateSolid, ftemperature_PrimaryCoolantInCore);
00683     fFuelReflMaterial4.push_back( new TMXMaterial("fuel_Berylium_m41",1.85*(g/cm3), 12, kStateSolid, ftemperature_PrimaryCoolantAndReflectorAndCR));
00684 
