00792   // Vacuum
00794 void DefineMaterials::DefineVacuum()
00795 {
00796     fVacuum = TMXMaterial::Find("world_Galactic");
00797     if( !fVacuum ) {
00798         fVacuum = new TMXMaterial("world_Galactic",universe_mean_density*g/cm3, 1, kStateGas, ftemperature_world, 3.e-18*hep_pascal);
00799         fVacuum->AddElement(10010,1);
00800     }
00801 }
