00944   fMixCm_Material->AddElement(962440, 90860);
00945   fMixCm_Material->AddElement(962450,  6569);
00946   fMixCm_Material->AddElement(962460,   745);
00947   fMixCm_Material->AddElement(962470,     7);
00948 }
00949 
00950 void DefineMaterials::DefineDirtPu_Material()
00951 {
00952   if(!fRecycledPu_Material) {DefineRecycledPu_Material();}
00953   if(!fMixAm_Material)      {DefineMixAm_Material();}
00954   if(!fMixCm_Material)      {DefineMixCm_Material();}
00955     
00956   G4double x_mass_Np = 0.053597;
00957   G4double x_mass_Pu = 0.860810;
00958   G4double x_mass_Am = 0.083738;
00959   G4double x_mass_Cm = 0.001855;
00960 
00961   G4double denDirtPu = 11*CLHEP::g/CLHEP::cm3;
00962   fDirtPu_Material = new TMXMaterial("fuel_DirtPu", denDirtPu, 4, kStateSolid, ftemperature_fuel);
00963   fDirtPu_Material->AddElement (932370              , x_mass_Np);
00964   fDirtPu_Material->AddMaterial(fRecycledPu_Material, x_mass_Pu);
00965   fDirtPu_Material->AddMaterial(fMixAm_Material     , x_mass_Am);
00966   fDirtPu_Material->AddMaterial(fMixCm_Material     , x_mass_Cm);
00967 }
00968 
00970   // ThPuO2
00972   /*
00973    *     6.  ThPu-O2 (=10.407g/cc): typical start-up fuel for the Energy Amplifier
00974          Mlange = Th-232 0.87 + mlange Dirty Pu 0.13 + O 2.0 (at.%)
00975    */
00976 void DefineMaterials::DefineThPuO2_Material()
00977 {
00978   if(!fDirtPu_Material) {DefineDirtPu_Material();}
00979   double MolarMass_Th232 = (TMXElementStore::GetElement(902320))->Mweight();
00980   double MolarMass_O16   = (TMXElementStore::GetElement( 80160))->Mweight();
00981   double MolarMass_DirtyPu = GetMolarMass(fDirtPu_Material);
00982   double MolarMass = 0.13 * MolarMass_DirtyPu + 0.87 * MolarMass_Th232 + 2.00 * MolarMass_O16; // Actually 3 times the molar mass
00983   
00984   G4double denThPuO2 = 10.407*CLHEP::g/CLHEP::cm3;
00985   fThPuO2_Material = new TMXMaterial("fuel_ThPuO2", denThPuO2, 3, kStateSolid, ftemperature_fuel);
00986   fThPuO2_Material->AddMaterial(fDirtPu_Material, 0.13 * MolarMass_DirtyPu / MolarMass);
00987   fThPuO2_Material->AddElement(902320           , 0.87 * MolarMass_Th232   / MolarMass);
00988   fThPuO2_Material->AddElement( 80160           , 2.00 * MolarMass_O16     / MolarMass);
00989 }
