01117 
01118     
01122 //      Tools
01123 //
01126 
01128 //  Return the molar mass in g/mole of a material composed of one single isotope per element
01129 //
01130 double DefineMaterials::GetMolarMass(TMXMaterial* mat) {
01131     const G4ElementVector * elemVector = mat->GetElementVector();
01132     const double* AtomsVectorPerVolume = mat->GetVecNbOfAtomsPerVolume();
01133     double TotNbAtoms = mat->GetTotNbOfAtomsPerVolume();
01134     
01135     double MolarMass = 0;
01136     for(size_t elem=0; elem < elemVector->size(); ++elem) {
01137         MolarMass += AtomsVectorPerVolume[elem]/TotNbAtoms  * (*elemVector)[elem]->GetA()/(g/mole);
01138     }
01139     return MolarMass;
01140 }
01141 
01143 //  Fill the material with element by mass fraction calculated from a vector of IZA and atom fraction (or number of atoms)
01144 //
01145 void DefineMaterials::AddAtomicFractions( TMXMaterial* mat, std::vector<std::pair<int, G4double>>* V_nAtoms) {
01146   
01147   // Calculation of the total mass
01148   G4double M_tot = 0; // Total mass * Na
01149   for(size_t i = 0; i< V_nAtoms->size(); ++i) {      
01150       M_tot += (*V_nAtoms)[i].second * (TMXElementStore::GetElement((*V_nAtoms)[i].first)->Mweight()*(g/mole));
01151   }
01152   
01153   // Addition of elements via its mass fraction
01154   for(size_t i = 0; i< V_nAtoms->size(); ++i) {
01155       mat->AddElement((*V_nAtoms)[i].first, (*V_nAtoms)[i].second * (TMXElementStore::GetElement((*V_nAtoms)[i].first)->Mweight()*(g/mole)) / M_tot);
01156   }
01157   
01158   // Clear the vector
01159   V_nAtoms->clear();
01160 }
