00047 TMXAbstractTargetAssembly::TMXAbstractTargetAssembly(TMXInputParameters* parameters, const std::string& TargetAssemblyName):
00048 m_assemblyName(TargetAssemblyName),
00049 m_p(parameters),
00050 m_copy_number{0}
00051 {
00052     m_targetName.push_back(m_assemblyName + "_Slab");
00053     m_coolantName.push_back(m_assemblyName + "_Coolant");
00054     m_cladName.push_back(m_assemblyName + "_Tube");
00055 }
