00057 TMXAbstractTargetAssembly::~TMXAbstractTargetAssembly()
00058 {
00059    m_targetLogicalVolumes.clear();
00060    m_cladLogicalVolumes.clear();
00061    m_coolantLogicalVolumes.clear();
00062    m_Solids.clear();
00063 }
