00096 void TMXAbstractTargetAssembly::createMaterial(double Kelvin_temperature) {
00097     
00098     // Default Materials
00099     
00100     // Tungsten target
00101     m_TargetMaterial = new TMXMaterial(m_assemblyName + "_Tungsten",19.30*g/cm3, 5, kStateSolid, Kelvin_temperature);
00102     m_TargetMaterial->AddElement(741800,  12); // W-180
00103     m_TargetMaterial->AddElement(741820,2650); // W-182
00104     m_TargetMaterial->AddElement(741830,1431); // W-183
00105     m_TargetMaterial->AddElement(741840,3064); // W-184
00106     m_TargetMaterial->AddElement(741860,2843); // W-186
00107 //  Isotope         Relative
00108 // Atomic Mass           Isotopic
00109 //   Composition    Standard
00110 // Atomic Weight     Notes 
00111 //              
00112 // 74   W   
00113 // 180  179.946 7108(20)      0.0012(1)      183.84(1)   
00114 // 182  181.948 203 94(91)    0.2650(16)
00115 // 183  182.950 222 75(90)    0.1431(4)
00116 // 184  183.950 930 92(94)    0.3064(2)
00117 // 186  185.954 3628(17)      0.2843(19) 
00118     
00119     // Stainless steel cladding of the target
00120     m_CladMaterial = new TMXMaterial(m_assemblyName + "_stainless-steel",8*g/cm3, 8, kStateSolid, Kelvin_temperature);
00121     m_CladMaterial->AddElement(260540,99); // Fe-54
00122     m_CladMaterial->AddElement(260560,33); // Fe-56
00123     m_CladMaterial->AddElement(260570,84); // Fe-57
00124     m_CladMaterial->AddElement(260580,43); // Fe-58
00125     m_CladMaterial->AddElement(240500,83); // Cr-50
00126     m_CladMaterial->AddElement(240520,23); // Cr-52
00127     m_CladMaterial->AddElement(240530,15); // Cr-53
00128     m_CladMaterial->AddElement(240540,16); // Cr-54
00129 //    // parameters are taken from
00130 //    //  http://www.azom.com/article.aspx?ArticleID=965
00131 //    AddMaterial("G4_STAINLESS-STEEL", 8.00, 0, 0.0, 3);
00132 //    AddElementByAtomCount("Fe", 74);
00133 // 26   Fe  
00134 // 54     53.939 608 99(53)       0.058 45(35)     55.845(2)     
00135 // 56     55.934 936 33(49)       0.917 54(36)
00136 // 57     56.935 392 84(49)       0.021 19(10)
00137 // 58     57.933 274 43(53)       0.002 82(4)
00138 //    AddElementByAtomCount("Cr", 18);
00139 // 24   Cr  
00140 // 50     49.946 041 83(94)       0.043 45(13)     51.9961(6)    
00141 // 52     51.940 506 23(63)       0.837 89(18)
00142 // 53     52.940 648 15(62)       0.095 01(17)
00143 // 54     53.938 879 16(61)       0.023 65(7) 
00144     
00145     // Water coolant
00146     m_CoolantMaterial = new TMXMaterial(m_assemblyName + "_water_TS" , 1.0*g/cm3 , 2, kStateLiquid, Kelvin_temperature);                
00147     m_CoolantMaterial->AddElement(10010,2,"TS_H_of_Water");                
00148     m_CoolantMaterial->AddElement(80160,1); 
00149 }
