00151 G4Element* TMXAbstractTargetAssembly::ElementExists(int Z, double A) {
00152     // Search for existing element by its Z and A
00153     G4ElementTable elemTable = *G4Element::GetElementTable();
00154     for(size_t i = 0; i<elemTable.size(); ++i) {
00155         if (elemTable[i] && elemTable[i]->GetZ() == Z && elemTable[i]->GetA() == A) {
00156             return elemTable[i];
00157         }
00158     }
00159     return nullptr;
00160 }
00161 
00162 G4Element* TMXAbstractTargetAssembly::ElementExists(std::string name) {
00163     // Search for existing element by its name
00164     G4ElementTable elemTable = *G4Element::GetElementTable();
00165     for(size_t i = 0; i<elemTable.size(); ++i) {
00166         if (elemTable[i] && (std::string)elemTable[i]->GetName() == name) {
00167             return elemTable[i];
00168         }
00169     }
00170     return nullptr;
00171 }
00172 
00173 size_t TMXAbstractTargetAssembly::getFirstOccurence(std::vector<std::string>* nameVector) { // Get first occurence of m_assembly_name
00174     for(size_t i = 0; i < nameVector->size(); ++i) {
00175         if( (*nameVector)[i].find(m_assemblyName + "_") != std::string::npos ) {
00176             return i;
00177         }
00178     }
00179     return 0;
00180 }
