00101 void SteppingAction::UserSteppingAction(const G4Step * theStep)
00102 {
00103     // Added on 14/04/2021:
00104     // 3rd modif: no effect on keff -> checked
00105 
00106     if(theStep->GetTrack()->GetParticleDefinition() == G4Neutron::NeutronDefinition()
00107     && theStep->GetTrack()->GetCurrentStepNumber() == 1
00108     && theStep->GetTrack()->GetCreatorProcess()
00109     && theStep->GetTrack()->GetCreatorProcess()->GetProcessName() == "nFission"
00110     && p->int_Option == 20)
00111     {
00112         theStep->GetTrack()->SetTrackStatus(fKillTrackAndSecondaries); // secondary neutron from fission are killed at their first step
00113         return;
00114     }
00115   // std::cout << "Begin of step" << std::endl;
00116   TMXStopTheRun::getInstance()->UserSteppingAction();
00117 
00118   /*if (theStep->GetTrack()->GetParticleDefinition()->GetParticleName() != "proton") {
00119       // theStep->GetTrack()->SetTrackStatus(fStopAndKill);
00120       return;
00121   }*/
00122 
00123   // If new run -> Initialization of stepping action
00124   if(fRunNumber != fEventAction->GetRunNumber()) { Initialize(fEventAction, fRunAction, *p); }
00125 
00126   // theStep->GetTrack()->SetTrackStatus(fKillTrackAndSecondaries);
00127 
00128   // If the hadron processes are not defined, then we search for them
00129   // std::cout << "Begin GetNeutronHadronicProcesses" <<std::endl;
00130   if(!fProcessElas)  {GetNeutronHadronicProcesses();}
00131 
00132   G4int nNeutrons = 0; // Number of secondary neutrons from inelastic reaction
00133   // std::cout << "Begin GetPhysicalVolumeNumber" <<std::endl;
00134   GetPhysicalVolumeNumber(theStep); // Get the physical volume ID of preStep, postStep and the one to be used
00135 
00136   // Find a better solution
00137   // FillNtupleDepositedPower(theStep);
00138 
00139   // General Scoring of Flux Spectrum for any particle define by the user
00140   fRunAction->m_scoringSurfaceCurrentSpectrum->score(theStep);
00141   fRunAction->m_scoringSurfaceFluenceSpectrum->score(theStep);
00142   fRunAction->m_scoringVolumeFluenceSpectrum->score(theStep);
00143   fRunAction->m_scoringPowerDensityMap->score(theStep);
00144 
00145 
00146   // std::cout<<theStep->GetPreStepPoint()->GetPhysicalVolume()->GetName()<<" "
00147   // <<theStep->GetTrack()->GetParticleDefinition()->GetParticleName()<<std::endl;
00148   //TFTEST
00149 
00150 //   if(theStep->GetPostStepPoint()->GetPhysicalVolume()) { // Not necessary anymore ?? GetProcessName has been protected.
00151 //     if( theStep->GetTrack()->GetDynamicParticle()->GetDefinition()->GetParticleName() !=  "neutron" && p->int_Option == 4) {
00152 //         theStep->GetTrack()->SetTrackStatus(fKillTrackAndSecondaries);
00153 //         return;
00154 //     }
00155 
00156 
00157 /*  if(theStep->GetPostStepPoint()->GetPhysicalVolume()) {
00158   if(theStep->GetPreStepPoint()->GetPhysicalVolume()->GetName() == "pTarget"
00159           && theStep->GetPostStepPoint()->GetPhysicalVolume()->GetName() == "pCore" )
00160   {
00161       if(theStep->GetTrack()->GetDefinition()->GetParticleName() == "e-" || theStep->GetTrack()->GetDefinition()->GetParticleName() == "gamma")
00162       {
00163 //          std::cout<<theStep->GetTrack()->GetDefinition()->GetParticleName() << " "
00164 //                   <<theStep->GetPreStepPoint()->GetPhysicalVolume()->GetName() << " "
00165 //                   <<theStep->GetPostStepPoint()->GetPhysicalVolume()->GetName()<<std::endl;
00166         theStep->GetTrack()->SetTrackStatus(fKillTrackAndSecondaries);
00167       }
00168   }
00169 }
00170   if(fEventAction->GetNevt() < 131500)
00171       theStep->GetTrack()->SetTrackStatus(fKillTrackAndSecondaries);
00172 */
00173 /*   if(theStep->GetPreStepPoint ()->GetPhysicalVolume()->GetName() == "Target" )
00174    {
00175         if(theStep->GetTrack()->GetDefinition()->GetParticleName() == "neutron")
00176         {
00177             std::cout<< "nFlu " << theStep->GetPreStepPoint ()->GetKineticEnergy()/ MeV << " " <<  (theStep->GetStepLength()/cm) / (theStep->GetPreStepPoint ()->GetPhysicalVolume()->GetLogicalVolume()->GetSolid()->GetCubicVolume()/cm3)<<std::endl;
00178         }
00179         if(theStep->GetTrack()->GetDefinition()->GetParticleName() == "gamma")
00180         {
00181 //            std::cout<< "pFlu " << theStep->GetPreStepPoint ()->GetKineticEnergy()/ MeV << " "<<   theStep->GetStepLength()/PostCubicVolume<<std::endl;
00182         }
00183     }
00184     if(theStep->GetTrack()->GetDefinition()->GetParticleName() == "neutron"
00185             && theStep->GetPostStepPoint()->GetPhysicalVolume()->GetName() != "Target"
00186             && theStep->GetPreStepPoint()->GetStepStatus() == fGeomBoundary)
00187     {
00188         std::cout<< "nYield " << theStep->GetPreStepPoint ()->GetKineticEnergy() / MeV << " "<<  1<<std::endl;
00189     }
00190     return;
00191 */
00192 
00193 
00194 
00196     // ---------------------- Scoring of every particles (neutron excepted) -----------------------
00198 
00199     // Initialization of status flags
00200     // std::cout << "Begin InitializeGenerationFlag" << std::endl;
00201     fGenerationCounter->InitializeGenerationFlag(theStep, IsTargetRegion(fPhysicalVolumeNumber));
00202     // std::cout << "Begin fGenerationCounter->GetStatus_SourceNeutron" << std::endl;
00203     fIsSourceNeutron = fGenerationCounter->GetStatus_SourceNeutron();
00204     // std::cout << "Begin fGenerationCounter->GetNeutronGeneration" << std::endl;
00205     fNeutronGeneration = fGenerationCounter->GetNeutronGeneration();
00206 
00207     // Required quantities from the step
00208 //     int Nprotons = 1;   // The graphs are scaled in Graphes_ROOT.C
00209     double dbl_Ibeam = 1; //.E6 / fEbeam /(double) Nprotons;  // beam current at 1 MW_beam
00210 
00211     // std::cout << "Begin FillEdepInStepHisto" << std::endl;
00212     FillEdepInStepHisto(theStep, dbl_Ibeam); // Fill histogram of Edeposition depending on the volume
00213     // std::cout << "Begin ScorePrimaryProtons" << std::endl;
00214     ScorePrimaryProtons(theStep, dbl_Ibeam); // Score information of primary protons
00215 
00216     // Fill Time VS Energy histograms of fission product
00217     // std::cout << "Begin FillTimeEnergyFissionProducts" << std::endl;
00218     if (IsFissionProduct(theStep->GetTrack()->GetDefinition()->GetParticleType())) { FillTimeEnergyFissionProducts(theStep); }
00220     // ----------------------------------- Neutrons scoring ---------------------------------------
00222     // std::cout << "Begin AccumulateMeanEperFission" << std::endl;
00223     AccumulateMeanEperFission(theStep); // To calculate the mean deposited energy per fission
00224 
00225     // std::cout << "Begin ManageNeutronScoring" << std::endl;
00226     ManageNeutronScoring(theStep, nNeutrons);
00227 
00228 //     CountNeutronSource_DefEAP(theStep);
00229 
00231     // ----------- k calculation, counting of neutrons and transmuted elements (burnup) -----------
00233 
00234     // Score the position of fissions inside a mesh
00235     // std::cout << "Begin keff_Mesh_scoring" << std::endl;
00236     keff_Mesh_scoring(theStep);
00237 
00238     // Score neutron generation and source term for burnup
00239     // std::cout << "Begin CountSecondaries" << std::endl;
00240     CountSecondaries(theStep, nNeutrons);
00241  //   computeSourceTerm(theStep);
00242 
00243     ManagePrimaryProtonEscape(theStep);
00244 
00245     /*if (theStep->GetTrack()->GetParticleDefinition()->GetParticleName() == "proton") {
00246         FillNtupleProton(
00247             theStep->GetPostStepPoint()->GetPosition(),
00248             theStep->GetPostStepPoint()->GetMomentumDirection(),
00249             theStep->GetTotalEnergyDeposit()/eV// theStep->GetPostStepPoint()->GetKineticEnergy()/eV
00250         );
00251     }*/
00252 
00253     if( p->int_Option == 20)
00254     {
00255         TMXkeff::getInstance()->inUserSteppingAction(theStep);
00256 //        computeSourceTerm(theStep);
00257         FillEdepInStepHisto(theStep,1); // Fill histogram of Edeposition depending on the volume
00258         ManageNeutronScoring(theStep, nNeutrons);
00259         return;
00260     }
00261 
00262     // std::cout << "End of step" << std::endl;
00263 }
