00382             for(int i=0; i<6; ++i) {
00383                 strPhysVolName.push_back("IRT4MAssembly6T_" + std::to_string(i));
00384                 strPhysVolNumber.push_back(41);
00385                 fmap_PV_name_number["IRT4MAssembly6T_" + std::to_string(i)] = 41;
00386                 strPhysVolName.push_back("FuelAssembly6T_" + std::to_string(i));
