01171 
01172 // Fill histograms of which energy distribution of the neutron flux (with volumic dependencies)
01173 void SteppingAction::FillHistoTimeEnergyFlux(const G4Step* theStep, bool BoundaryStatus, G4int nNeutrons) {
01174     auto analysisManager = G4AnalysisManager::Instance();
01175     // Get required quantites from the step
01176     G4double energyK = KineticEnergy_eV(theStep); // Kinetic energy in eV
01177     G4double StepLength        = theStep->GetStepLength()/cm; // Length of the step in cm
01178     G4double PostGlobalTime    = theStep->GetPostStepPoint()->GetGlobalTime()/s; // Time since the event is created
01179     G4String processName = "";
01180     if(theStep->GetPostStepPoint()->GetProcessDefinedStep())
01181         processName = theStep->GetPostStepPoint()->GetProcessDefinedStep()->GetProcessName();
01182 
01183     if (p->Geometry == 7 || p->Geometry == 8) { // temporary solution for the SVBR AKME
01184         if (IsTargetRegion(fPhysicalVolumeNumber)) // target
01185         {
01186             if( BoundaryStatus )  // On a surface
01187                 analysisManager->FillH2(12, log10(PostGlobalTime), log10(energyK));
01188             else // for interactions only
01189                 analysisManager->FillH2(16, log10(PostGlobalTime), log10(energyK));
01190 
01191             if(IsTarget(fPhysicalVolumeNumber)) // fluence spectrum in target except target tube
01192                 analysisManager->FillH1(13, log10(energyK), StepLength/fSpallationTargetCubicVolume);
01193         }
01194         else if (IsLeadRegion(fPhysicalVolumeNumber))  // Lead
01195         {
01196             // Angular Distribution (in cos)
01197             /*if (theStep && theStep->GetPostStepPoint()) {
01198                 const G4Isotope* target = GetTargetIsotope(theStep->GetPostStepPoint());
01199                 if (target) {
01200                     if (target && (target->GetName() == "BI-209-0")) {
01201                         analysisManager->FillH1(46, theStep->GetPostStepPoint()->GetMomentumDirection().dot(theStep->GetPreStepPoint()->GetMomentumDirection()), 1);
01202                     }
01203                 }
01204             }*/
01205 
01206             if ( BoundaryStatus )  // On a surface
01207                 analysisManager->FillH2(14, log10(PostGlobalTime), log10(energyK));
01208             else // for interactions only
01209                 analysisManager->FillH2(18, log10(PostGlobalTime), log10(energyK));
01210 
01211             // fluence spectrum in lead
01212             if (IsLead(fPhysicalVolumeNumber)) {
01213                 analysisManager->FillH1(2, log10(energyK), StepLength/fPrimaryCoolantCoreCubicVolume);
01214             }
01215 
01216             if (fPhysicalVolumeNumber >= 50) {  // fluence spectrum at different radius in lead
01217                 analysisManager->FillH1(3 + fPhysicalVolumeNumber-50, log10(energyK), StepLength/fPrimaryCoolantCubicVolume);
01218             }
01219         }
01220         else if (IsFuel(fPhysicalVolumeNumber))  // Fuel
01221         {
01222             if ( BoundaryStatus )  // On a surface
01223                 analysisManager->FillH2(15, log10(PostGlobalTime), log10(energyK));
01224             else // for interactions only
01225                 analysisManager->FillH2(19, log10(PostGlobalTime), log10(energyK));
01226 
01227             // fluence spectrum in fuel
01228             analysisManager->FillH1(8, log10(energyK), StepLength/fFuelUnionCubicVolume);
01229 
01230             /* // use scoring in param
01231             if (fPrePhysicalVolumeName == "pFuelMatBlkt1")
01232                 analysisManager->FillH1(30, log10(energyK), StepLength/fFuelBlkt1CubicVolume);
01233             else if (fPrePhysicalVolumeName == "pFuelMat1")
01234                 analysisManager->FillH1(31, log10(energyK), StepLength/fFuel1CubicVolume);
01235             else if (fPrePhysicalVolumeName == "pFuelMat2")
01236                 analysisManager->FillH1(32, log10(energyK), StepLength/fFuel2CubicVolume);
01237             else if (fPrePhysicalVolumeName == "pFuelMat3")
01238                 analysisManager->FillH1(33, log10(energyK), StepLength/fFuel3CubicVolume);
01239             else if (fPrePhysicalVolumeName == "pFuelMatGrid2")
01240                 analysisManager->FillH1(34, log10(energyK), StepLength/fFuelGrid2CubicVolume);
01241             else if (fPrePhysicalVolumeName == "pFuelMat4")
01242                 analysisManager->FillH1(35, log10(energyK), StepLength/fFuel4CubicVolume);
01243             else if (fPrePhysicalVolumeName == "pFuelMat5")
01244                 analysisManager->FillH1(36, log10(energyK), StepLength/fFuel5CubicVolume);
01245             else if (fPrePhysicalVolumeName == "pFuelMat6")
01246                 analysisManager->FillH1(37, log10(energyK), StepLength/fFuel6CubicVolume);
01247             else if (fPrePhysicalVolumeName == "pFuelMat7")
01248                 analysisManager->FillH1(38, log10(energyK), StepLength/fFuel7CubicVolume);
01249             else if (fPrePhysicalVolumeName == "pFuelMat8")
01250                 analysisManager->FillH1(39, log10(energyK), StepLength/fFuel8CubicVolume);
01251             else if (fPrePhysicalVolumeName == "pFuelMatGrid3")
01252                 analysisManager->FillH1(40, log10(energyK), StepLength/fFuelGrid3CubicVolume);
01253             else if (fPrePhysicalVolumeName == "pFuelMat9")
01254                 analysisManager->FillH1(41, log10(energyK), StepLength/fFuel9CubicVolume);
01255             else if (fPrePhysicalVolumeName == "pFuelMat10")
01256                 analysisManager->FillH1(42, log10(energyK), StepLength/fFuel10CubicVolume);
01257             else if (fPrePhysicalVolumeName == "pFuelMat11")
01258                 analysisManager->FillH1(43, log10(energyK), StepLength/fFuel11CubicVolume);
01259             else if (fPrePhysicalVolumeName == "pFuelMatBlkt2")
01260                 analysisManager->FillH1(44, log10(energyK), StepLength/fFuelBlkt2CubicVolume);
01261             */
01262             if (processName=="nFission") {
01263                 // Energy spectrum in fuel for fissions
01264                 analysisManager->FillH1(9, log10(energyK));
01265 
01266                 // Time for fissions from N0
01267                 if (fNeutronGeneration == 0)
01268                     analysisManager->FillH1(12, log10(PostGlobalTime));
01269 
01270                 analysisManager->FillH1(14, log10(PostGlobalTime)); // Time for every fissions
01271             }
01272         }
01273         else if (IsInReactorVes(fPhysicalVolumeNumber)) // Inner Reactor Vessel
01274         {
01275             if( BoundaryStatus )  // On a surface
01276                 analysisManager->FillH2(41, log10(PostGlobalTime), log10(energyK));
01277             else // for interactions only
01278                 analysisManager->FillH2(42, log10(PostGlobalTime), log10(energyK));
01279 
01280             // use scoring in param analysisManager->FillH1(28, log10(energyK), StepLength/fReactorInnerVesselCubicVolume);
01281         }
01282         else if (IsSecondCoolant(fPhysicalVolumeNumber)) // Secondary Coolant in Heat Exchangers
01283         {
01284             if( BoundaryStatus )  // On a surface
01285                 analysisManager->FillH2(43, log10(PostGlobalTime), log10(energyK));
01286             else // for interactions only
01287                 analysisManager->FillH2(44, log10(PostGlobalTime), log10(energyK));
01288 
01289             // use scoring in param analysisManager->FillH1(29, log10(energyK), StepLength/fSecondaryCoolantCubicVolume);
01290         }
01291         else if (IsIncineration(fPhysicalVolumeNumber)) // Incineration Blanket
01292         {
01293             // use param file analysisManager->FillH1(47, log10(energyK), StepLength/fIncinerationCubicVolume);
01294         }
01295     }
01296     else {
01297         if( IsTargetRegion(fPhysicalVolumeNumber) ) // target
01298         {
01299             if( BoundaryStatus )  // On a surface
01300                 analysisManager->FillH2(12, log10(PostGlobalTime), log10(energyK));
01301             else // for interactions only
01302                 analysisManager->FillH2(16, log10(PostGlobalTime), log10(energyK));
01303 
01304             if(IsTarget(fPhysicalVolumeNumber)) {// fluence spectrum in target except target tube
01305                 analysisManager->FillH1(13, log10(energyK), StepLength/fTargetTubeCubicVolume);
01306                 // std::cout << "Score Base "
01307                 //             << theStep->GetTrack()->GetParticleDefinition()->GetParticleName() << " "
01308                 //             << theStep->GetPreStepPoint()->GetPhysicalVolume()->GetName()
01309                 //             << "\n";
01310             } //       analysisManager->FillH1(13, log10(energyK), StepLength/fmap_Region_volume[fPhysicalVolumeNumber]);
01311 
01312             // else if (IsBeamWindow(fPhysicalVolumeNumber)) {
01313             //     analysisManager->FillH1(57, log10(energyK), StepLength/fTargetBeamWindowCubicVolume);
01314             // }
01315 
01316         }
01317         else if( (p->Geometry != 1 && IsHeatShieldRegion(fPhysicalVolumeNumber))
01318             || (p->Geometry == 1 && IsBeamWindow(fPhysicalVolumeNumber))) // Reflector + iron Top
01319         {
01320             if( BoundaryStatus )  // On a surface
01321                 analysisManager->FillH2(13, log10(PostGlobalTime), log10(energyK));
01322             else // for interactions only
01323                 analysisManager->FillH2(17, log10(PostGlobalTime), log10(energyK));
01324 
01325             // fluence spectrum in water-cooled heat shield
01326             analysisManager->FillH1(10, log10(energyK), StepLength/fmap_Region_volume[fPhysicalVolumeNumber]);
01327         }
01328         else if(IsReflector(fPhysicalVolumeNumber))  // Lead
01329         {
01330              // fluence spectrum in reflector
01331             // use scoring in param analysisManager->FillH1(45, log10(energyK), StepLength/fmap_Region_volume[fPhysicalVolumeNumber]);
01332 
01333         }
01334         else if(IsLeadRegion(fPhysicalVolumeNumber))  // Lead
01335         {
01336             if( BoundaryStatus )  // On a surface
01337                 analysisManager->FillH2(14, log10(PostGlobalTime), log10(energyK));
01338             else // for interactions only
01339                 analysisManager->FillH2(18, log10(PostGlobalTime), log10(energyK));
01340 
01341             // fluence spectrum in lead
01342             if(IsLead(fPhysicalVolumeNumber)) {analysisManager->FillH1(2, log10(energyK), StepLength/fmap_Region_volume[fPhysicalVolumeNumber]);}
01343 
01344             if(IsSamplesInLead(fPhysicalVolumeNumber)) {  // fluence spectrum at different radius in lead
01345                 analysisManager->FillH1(3 + fPhysicalVolumeNumber-50, log10(energyK), StepLength/ fmap_Region_volume[fPhysicalVolumeNumber]);
01346             }
01347         }
01348         else if(IsFuel(fPhysicalVolumeNumber))  // Fuel
01349         {
01350             if( BoundaryStatus )  // On a surface
01351                 analysisManager->FillH2(15, log10(PostGlobalTime), log10(energyK));
01352             else // for interactions only
01353                 analysisManager->FillH2(19, log10(PostGlobalTime), log10(energyK));
01354 
01355             // fluence spectrum in fuel
01356             analysisManager->FillH1(8, log10(energyK), StepLength/fmap_Region_volume[fPhysicalVolumeNumber]);
01357 
01358             if (processName=="nFission") {
01359                 // Energy spectrum in fuel for fissions
01360                 analysisManager->FillH1(9, log10(energyK));
01361 
01362                 // Time for fissions from N0
01363                 if(fNeutronGeneration == 0)
01364                     analysisManager->FillH1(12, log10(PostGlobalTime));
01365 
01366                 analysisManager->FillH1(14, log10(PostGlobalTime)); // Time for every fissions
01367             }
01368         }
01369         else if(IsWaterCoreRegion(fPhysicalVolumeNumber))  // Water in core acting as the moderator
01370         {
01371             if( BoundaryStatus )  // On a surface
01372                 analysisManager->FillH2(39, log10(PostGlobalTime), log10(energyK));
01373             else // for interactions only
01374                 analysisManager->FillH2(40, log10(PostGlobalTime), log10(energyK));
01375 
01376             // fluence spectrum in water
01377             analysisManager->FillH1(26, log10(energyK), StepLength/fmap_Region_volume[fPhysicalVolumeNumber]);
01378         }
01379         else if(IsSpentFuel(fPhysicalVolumeNumber))  // Spent fuel samples to be transmuted
01380         {
01381             // fluence spectrum in fuel sample to transmute
01382             analysisManager->FillH1(27, log10(energyK), StepLength/fmap_Region_volume[fPhysicalVolumeNumber]);
01383         }
01384         else if(IsNeutronAbsorber(fPhysicalVolumeNumber) && fNeutronGeneration == 0 && (processName=="nCapture" || (processName=="neutronInelastic" && nNeutrons==0)))  { // Energy spectrum for absorption of spallation neutrons in Neutron absorber
01385             analysisManager->FillH2(21, log10(energyK), log10(StepLength/fmap_Region_volume[fPhysicalVolumeNumber]));
01386             ++fN0Absorber;
01387             fEventAction->SetN0Abs(fN0Absorber);
01388         }
01389 
01390         if(IsNeutronAbsorber(fPhysicalVolumeNumber) && (processName=="nCapture" || (processName=="neutronInelastic" && nNeutrons==0))) // Energy spectrum for absorption in Neutron absorber
01391             analysisManager->FillH2(20, log10(energyK), log10(StepLength/fmap_Region_volume[fPhysicalVolumeNumber]));
01392 
01393         if((processName=="nCapture" || (processName=="neutronInelastic" && nNeutrons==0) || processName=="nFission") && fPromptN) // Prompt neutron absorption -> Calculation of prompt neutron lifetime
01394             analysisManager->FillH1(19, log10(PostGlobalTime - fBirthTime));
01395 
01396         if( processName=="nCapture" || (processName=="neutronInelastic" && nNeutrons==0) || processName=="nFission") // Neutron absorption -> Calculation of neutron lifetime
01397             analysisManager->FillH1(20, log10(PostGlobalTime - fBirthTime));
01398     }
01399 }
01400 
01401 // The mesh which is used for keff calculations is hard-coded and the scoring of fission must be adapted to this mesh
01402 void SteppingAction::keff_Mesh_scoring(const G4Step* theStep) {
01403     G4String processName = "";
01404     if(theStep->GetPostStepPoint()->GetProcessDefinedStep()) {
01405         processName = theStep->GetPostStepPoint()->GetProcessDefinedStep()->GetProcessName();}
01406     if(processName=="nFission") {
01407         if (TMXOption::IsKeffCalculation(p->int_Option)) {  // In case of keff calculation, update the 3D space distribution with the position of fission
01408             G4ThreeVector PostPos = theStep->GetPostStepPoint()->GetPosition();
01409             if(p->Geometry == 0) { // Trotsk
01410                 double radius = sqrt(PostPos.x()/mm * PostPos.x()/mm + PostPos.y()/mm*PostPos.y()/mm);
01411                 double theta = std::acos(PostPos.x()/mm / radius);
01412                 if(PostPos.y() < 0) {theta = twopi - theta;}
01413                 fEventAction->AddWeight(radius*radius, theta, fTargetZdistanceToBottom - fh0 + PostPos.z()/mm);  // RandomDistribution
01414             }
01415             else if(p->Geometry == 1) { // CVR-Rez
01416                 fEventAction->AddWeight(PostPos.x()/mm, PostPos.y()/mm, PostPos.z()/mm);  // RandomDistribution
01417             }
01418             else { // Default is polar mesh
01419                 double radius = sqrt(PostPos.x()/mm * PostPos.x()/mm + PostPos.y()/mm*PostPos.y()/mm);
01420                 double theta = std::acos(PostPos.x()/mm / radius);
01421                 if(PostPos.y() < 0) {theta = twopi - theta;}
01422                 fEventAction->AddWeight(radius*radius, theta, PostPos.z()/mm);  // RandomDistribution
01423             }
01424         }
01425     }
01426 }
