01476   fNeutronGeneration = 0;
01477   fIsSourceNeutron = false;
01478 //   fNeutronGeneration = -1;
01479 //   fIsSourceNeutron = false;
01480   v_NeutronGeneration.clear();
01481   N0 = 0;
01482   Nf = 0;
01483   Nfission = 0;
01484   Nnfission = 0;
01485   fN0Absorber = 0;
01486   fTargetZdistanceToBottom = 0;
01487   fh0 = 0;
01488   fEnergyAtBirth = 0;
01489   fPosAtBirth = G4ThreeVector(0,0,0);
