01508     Initialize_userDefinedRegions();
01509 
01510     // Calculation of volumes in cm3
01511     // To be called after the definition of regions
01512     fmap_LV_volume.clear();     // clear the map at initialization
01513     fmap_mat_volume.clear();    // clear the map at initialization
01514     fmap_Region_volume.clear(); // clear the map at initialization
