01516     fmap_LV_mass.clear();     // clear the map at initialization
01517     fmap_mat_mass.clear();    // clear the map at initialization
01518     fmap_Region_mass.clear(); // clear the map at initialization
01519     RecursiveMassCalculation((*G4PhysicalVolumeStore::GetInstance())[0]); // First physical volume of the G4PhysicalVolumeStore is the universe
01520 
01521     // Definition of some variables (e.g. volume for fluence calculation)
01522     Initialize_userDefinedVariables();
01523 
01524 //     std::cout << "---- End of SteppingAction Initialization -----" << std::endl;
01525 
01526     // Setup the volumes of interest for the scoring of spectra
01527     fRunAction->m_scoringSurfaceCurrentSpectrum->buildMapPVSurface();
01528     fRunAction->m_scoringSurfaceFluenceSpectrum->buildMapPVSurface();
01529     fRunAction->m_scoringVolumeFluenceSpectrum->buildMapPVCubicVolume(fmap_LV_volume);
01530 
01531     if (p->Verbose >= 1) {
01532         fRunAction->m_scoringVolumeFluenceSpectrum->printSummary();
01533     }
