01536 // Recalculate the region ID associated to a physical volume at each step
01537 void SteppingAction::GetPhysicalVolumeNumber(const G4Step * theStep) {
01538     G4String  thePrePVname  = theStep->GetPreStepPoint ()->GetPhysicalVolume()->GetName(); // Name of the physical volume of the preStep
01539     G4StepStatus PreStatus  = theStep->GetPreStepPoint ()->GetStepStatus(); // Status of the pre-step
01540     G4String  thePostPVname;
01541     if(theStep->GetPostStepPoint()->GetPhysicalVolume())
01542         thePostPVname = theStep->GetPostStepPoint()->GetPhysicalVolume()->GetName(); // Name of the physical volume of the postStep
01543     else
01544         thePostPVname = thePrePVname; // if the postStep is outside the universe = its name is the one of the preStep physical volume
01545     G4StepStatus PostStatus = theStep->GetPostStepPoint()->GetStepStatus(); // Status of the post-step
01546     const G4int stepnumber  = theStep->GetTrack()->GetCurrentStepNumber(); // stepnumber of the track, ==1 means first step
01547 
01548     if(stepnumber == 1) { // Initialize the volume number of the track's first step
01549         fPrePhysicalVolumeNumber = GetRegionNumber(thePrePVname, PreStatus);
01550 
01551         if(thePrePVname.compare(thePostPVname) == 0 || PostStatus == fWorldBoundary) // if postStep point is still in the same volume or at the boarder of the universe
01552             fPostPhysicalVolumeNumber = fPrePhysicalVolumeNumber;
01553         else // We search its number
01554             fPostPhysicalVolumeNumber = GetRegionNumber(thePostPVname, PostStatus);
01555     }
01556     else { // not first step of the track
01557         if( fPrePhysicalVolumeNumber != fPostPhysicalVolumeNumber ) // If the track has changed of volume during last step
01558             fPrePhysicalVolumeNumber = fPostPhysicalVolumeNumber;
01559 
01560         if(thePrePVname.compare(thePostPVname) != 0 && PostStatus == fGeomBoundary) // if postStep point is not in the same volume and postStep is at the boarder of a volume
01561             fPostPhysicalVolumeNumber = GetRegionNumber(thePostPVname, PostStatus);
01562     }
01563 
01564     fPhysicalVolumeNumber = fPrePhysicalVolumeNumber;
01565     fPrePhysicalVolumeName = thePrePVname;
01566 
01567 
01569     // fPhysicalVolumeNumber = GetRegionNumber(thePrePVname, PreStatus);
01571 
01572 //     if(/*stepnumber != 1*/ fPhysicalVolumeNumber==1 && thePrePVname != "AirSpacer" && thePostPVname != "AirSpacer") {
01573 //         std::cout << "\nstepnumber " << stepnumber << "\n\t thePrePVname : " << thePrePVname << " fPrePhysicalVolumeNumber " << fPrePhysicalVolumeNumber << " boundary ? " << (PreStatus == fGeomBoundary) << std::endl;
01574 //         std::cout << "\t thePostPVname : " << thePostPVname << " fPostPhysicalVolumeNumber " << fPostPhysicalVolumeNumber << " boundary ? " << (PostStatus == fGeomBoundary) << std::endl;
01575 //         std::cout << "\t fPhysicalVolumeNumber " << fPhysicalVolumeNumber << std::endl;
01576 //     }
01577 }
