01579 // Get the region ID of a physical volume considering its name
01580 int SteppingAction::GetRegionNumber(std::string PhyicalVolume_name, G4StepStatus /*stepStatus*/) {
01581     if(fmap_PV_name_number.find(PhyicalVolume_name) == fmap_PV_name_number.end()) { // if PhyicalVolume_name not found
01582 //         if(stepStatus != fWorldBoundary) {
01583 //             std::cout << "in SteppingAction: !! WARNING !! Physical Volume " << PhyicalVolume_name << " not listed in fmap_PV_name_number of SteppingAction." << std::endl;
01584 //             std::cout << "Region number is set to universe 1" << std::endl; }
01585         return 1; // it is considered as the universe
01586     }
01587     else // If found in the map of regions
01588         return fmap_PV_name_number[PhyicalVolume_name];
01589 }
