01591 // Calculate the cubic volume per material, logical volume or region
01592 void SteppingAction::RecursiveVolumeCalculation(G4VPhysicalVolume* PV, int replicate_number) {
01593     if(PV) { // if physical volume is not null
01594         G4LogicalVolume* LV = PV->GetLogicalVolume();
01595         int PV_replicate_number = replicate_number * PV->GetMultiplicity(); // Get the number of replicates
01596 
01597         if(LV) { // if logical volume is not null
01598             double PV_volume = replicate_number * LV->GetSolid()->GetCubicVolume()/cm3; // Get the real volume of daugthers
01599 
01601             // Sum of volume in dedicated maps
01603 
01604             // Map of listed regions
01605             if(fmap_PV_name_number.find(PV->GetName()) != fmap_PV_name_number.end())
01606                 fmap_Region_volume[fmap_PV_name_number[PV->GetName()]] += PV_volume;
01607             else
01608                 fmap_Region_volume[1] += PV_volume;
01609 
01610             // Map of Logical volumes
01611             fmap_LV_volume[LV->GetName()] += PV_volume;
01612 
01613             // Map of materials
01614             if(LV->GetMaterial()) { fmap_mat_volume[LV->GetMaterial()->GetName()] += PV_volume;} // If material is not null
01615 
01616             for(size_t i = 0; i < LV->GetNoDaughters(); ++i) { // For each Physical volume daugther of the Logical volume
01617                 G4VPhysicalVolume* Daughter_PV = LV->GetDaughter(i);
01618                 int daughter_replicate_number = PV_replicate_number * Daughter_PV->GetMultiplicity(); // Get the number of replicates
01619                 double daugther_volume = daughter_replicate_number * Daughter_PV->GetLogicalVolume()->GetSolid()->GetCubicVolume()/cm3; // Get the real volume of daugthers
01620 
01622                 // Substract volume of daughters in dedicated maps
01624 
01625                 // Map of listed regions
01626                 if(fmap_PV_name_number.find(PV->GetName()) != fmap_PV_name_number.end())
01627                     fmap_Region_volume[fmap_PV_name_number[PV->GetName()]] -= daugther_volume; // removing volume of daughters
01628                 else
01629                     fmap_Region_volume[1] -= daugther_volume; // removing volume of daughters
01630 
01631                 // Map of Logical volumes
01632                 fmap_LV_volume[LV->GetName()] -= daugther_volume; // removing volume of daughters
01633 
01634                 // Map of materials
01635                 if(LV->GetMaterial()) { fmap_mat_volume[LV->GetMaterial()->GetName()] -= daugther_volume;}  // If material is not null, the volume of daughters is removed
01636 
01637                 // Call recursive method for each daughter physical volume (Daughter_PV)
01638                 RecursiveVolumeCalculation(Daughter_PV, PV_replicate_number);
01639             }
01640         }
01641     }
01642 }
01643 
01644 // Calculate the mass per material, logical volume or region
01645 void SteppingAction::RecursiveMassCalculation(G4VPhysicalVolume* PV, int replicate_number) {
01646     if(PV) { // if physical volume is not null
01647         G4LogicalVolume* LV = PV->GetLogicalVolume();
01648         int PV_replicate_number = replicate_number * PV->GetMultiplicity(); // Get the number of replicates
01649 
01650         if(LV) { // if logical volume is not null
01651             double PV_mass = replicate_number * LV->GetMass()/g; // Get the real mass of daugthers
01652 
01654             // Sum of volume in dedicated maps
01656 
01657             // Map of listed regions
01658             if(fmap_PV_name_number.find(PV->GetName()) != fmap_PV_name_number.end())
01659                 fmap_Region_mass[fmap_PV_name_number[PV->GetName()]] += PV_mass;
01660             else
01661                 fmap_Region_mass[1] += PV_mass;
01662 
01663             // Map of Logical volumes
01664             fmap_LV_mass[LV->GetName()] += PV_mass;
01665 
01666             // Map of materials
01667             if(LV->GetMaterial()) { fmap_mat_mass[LV->GetMaterial()->GetName()] += PV_mass;} // If material is not null
01668 
01669             for(size_t i = 0; i < LV->GetNoDaughters(); ++i) { // For each Physical volume daugther of the Logical volume
01670                 G4VPhysicalVolume* Daughter_PV = LV->GetDaughter(i);
01671                 int daughter_replicate_number = PV_replicate_number * Daughter_PV->GetMultiplicity(); // Get the number of replicates
01672                 double daugther_volume = daughter_replicate_number * Daughter_PV->GetLogicalVolume()->GetMass()/g; // Get the real mass of daugthers
01673 
01675                 // Substract volume of daughters in dedicated maps
01677 
01678                 // Map of listed regions
01679                 if(fmap_PV_name_number.find(PV->GetName()) != fmap_PV_name_number.end())
01680                     fmap_Region_mass[fmap_PV_name_number[PV->GetName()]] -= daugther_volume; // removing volume of daughters
01681                 else
01682                     fmap_Region_mass[1] -= daugther_volume; // removing volume of daughters
01683 
01684                 // Map of Logical volumes
01685                 fmap_LV_mass[LV->GetName()] -= daugther_volume; // removing volume of daughters
01686 
01687                 // Map of materials
01688                 if(LV->GetMaterial()) { fmap_mat_mass[LV->GetMaterial()->GetName()] -= daugther_volume;}  // If material is not null, the volume of daughters is removed
01689 
01690                 // Call recursive method for each daughter physical volume (Daughter_PV)
01691                 RecursiveMassCalculation(Daughter_PV, PV_replicate_number);
01692             }
01693         }
01694     }
01695 }
