01697 // Calculate the kinetic energy
01698 double SteppingAction::KineticEnergy_eV(const G4Step * theStep) {
01699     G4double energyK = theStep->GetTrack()->GetKineticEnergy()/eV;
01700     if(energyK==0) {energyK = theStep->GetPreStepPoint()->GetKineticEnergy()/eV;} // If kinetic energy is 0, get the kinetic energy of preStep
01701     return energyK; // Return the energy in eV
01702 }
