01704 bool SteppingAction::IsInReactorVes       (int VolNumber) { return  VolNumber == 80;}
01705 bool SteppingAction::IsSecondCoolant      (int VolNumber) { return  VolNumber == 85;}
01706 bool SteppingAction::IsIncineration       (int VolNumber) { return  VolNumber == 90;}
01707 bool SteppingAction::IsTargetRegion       (int VolNumber) { return (VolNumber <= 26 &&  VolNumber >= 14);}
01708 bool SteppingAction::IsTarget             (int VolNumber) { return (VolNumber <= 23 &&  VolNumber >= 14);}
01709 bool SteppingAction::IsHeatShieldRegion   (int VolNumber) { return (VolNumber == 3  ||  VolNumber == 13);}
01710 bool SteppingAction::IsLeadRegion         (int VolNumber) { return (VolNumber == 5  || (VolNumber >= 50 && VolNumber <= 54));}
01711 bool SteppingAction::IsWaterCoreRegion    (int VolNumber) { return  VolNumber == 42;}
01712 bool SteppingAction::IsBeamPipe           (int VolNumber) { return  VolNumber == 24;}
01713 bool SteppingAction::IsBeamWindow         (int VolNumber) { return  VolNumber == 25;}
01714 bool SteppingAction::IsLead               (int VolNumber) { return  VolNumber ==  5;}
01715 bool SteppingAction::IsSamplesInLead      (int VolNumber) { return (VolNumber >= 50  && VolNumber <= 54);}
01716 bool SteppingAction::IsSpentFuel          (int VolNumber) { return  VolNumber == 60;}
01717 bool SteppingAction::IsNeutronAbsorber    (int VolNumber) { return  VolNumber == 55;}
01718 bool SteppingAction::IsFuel               (int VolNumber) { return  VolNumber == 400;}
01719 bool SteppingAction::IsCoolantRegion      (int VolNumber) { return (VolNumber == 5 || (VolNumber >= 50 && VolNumber <= 54));}
01720 bool SteppingAction::IsCoolantInCoreRegion(int VolNumber) { return  VolNumber == 42;}
01721 bool SteppingAction::IsReflector          (int VolNumber) { return  VolNumber == 44;}
01722 bool SteppingAction::IsFissionProduct(std::string particleType) { return (particleType=="nucleus" && IsFuel(fPhysicalVolumeNumber));}
01723 bool SteppingAction::IsPrimaryProton(std::string particleName, int ID_track) { return (particleName=="proton" && ID_track == 1);}
01724 bool SteppingAction::IsEscapingTarget() {
01725     return (IsTargetRegion(fPrePhysicalVolumeNumber) && !IsTargetRegion(fPostPhysicalVolumeNumber));}
