01750 //
01751 // Methods used to count neutrons, transmuted elements, and calculate k
01752 //
01767 
01768 void SteppingAction::CountSecondaries(const G4Step* theStep, int nNeutrons) {
01769 
01771     // Counting of neutron generations
01772     // Counting of source term for burnup calculation
01773     int Nb_second_neutrons=0;
01774     int Nb_OtherNeutrons  =0;
01775     int Nb_inelastic      =0;
01776     const std::vector<const G4Track*> *secondaries = theStep->GetSecondaryInCurrentStep();
01777     G4String processName = "";
01778     if(theStep->GetPostStepPoint()->GetProcessDefinedStep()) {
01779         processName = theStep->GetPostStepPoint()->GetProcessDefinedStep()->GetProcessName();}
01780 
01781     for(G4int i=0; i<(G4int)secondaries->size(); ++i) {
01782         // std::cout << "secondaries " << i << std::endl;
01783 
01784         // Count neutron per generation and change generation of secondaries
01785         if( theStep->GetTrack()->GetTrackStatus() != fKillTrackAndSecondaries )
01786             fGenerationCounter->NeutronGenerationCounting(theStep, (*secondaries)[i], nNeutrons, Nb_inelastic);
01787         else
01788             std::cout << "The track and its secondaries are killed, neutron counting is biaised." << std::endl;
01789 
01790         // count secondary neutrons coming from mother neutron and not from fission
01791         if(theStep->GetTrack()->GetDefinition()->GetParticleName() == "neutron"
01792           && (*secondaries)[i]->GetDefinition()->GetParticleName() == "neutron"
01793           && processName!="nFission")
01794             ++Nb_second_neutrons;
01795 
01796         // count secondary neutrons coming from mother particle other than neutron or primary particles or particle in the target (flagged as source neutrons)
01797         if(theStep->GetTrack()->GetDefinition()->GetParticleName() != "neutron"
01798           && (*secondaries)[i]->GetDefinition()->GetParticleName() == "neutron"
01799           && processName!="nFission"
01800           && theStep->GetTrack()->GetTrackID() != 1
01801           && fIsSourceNeutron == true)
01802             ++Nb_OtherNeutrons;
01803 
01807         // Source term calculation for Burnup calculation
01811 //        if(p->TimeOfRun > 0) {TMXBurnup::FillvectorBurnupElement(theStep, fEventAction, (*secondaries)[i]);} // Calculation of burnup required only if time of run > 0
01813     }
01814 
01815 
01816      // count processes as well as production / disparition of elements
01817      G4VProcess* process = const_cast<G4VProcess*>(theStep->GetPostStepPoint()->GetProcessDefinedStep());
01818      G4HadronicProcess* hproc = dynamic_cast<G4HadronicProcess*>(process);
01819      Run* run = static_cast<Run*>(G4RunManager::GetRunManager()->GetNonConstCurrentRun());
01820      run->CountProcesses(process);
01821 
01822      if(     hproc &&
01823              ( process->GetProcessName() == "neutronInelastic"
01824                || process->GetProcessName() == "nFission"
01825                || process->GetProcessName() == "nCapture"
01826                || process->GetProcessName() == "ionInelastic"
01827                || process->GetProcessName() == "protonInelastic"
01828                || process->GetProcessName() == "pi+Inelastic"
01829                || process->GetProcessName() == "pi-Inelastic"
01830                || process->GetProcessName() == "alphaInelastic"
01831                || process->GetProcessName() == "dInelastic"
01832                || process->GetProcessName() == "tInelastic"
01833                || process->GetProcessName() == "He3Inelastic"
01834              ))
01835      {
01836          const G4Isotope* target = GetTargetIsotope(theStep->GetPostStepPoint());
01837          if(target)
01838          {
01839              double IZAmother = 10000 * target->GetZ() + 10 * target->GetN() + target->Getm();
01840 
01841              run->ParticleCount(IZAmother, -1, (TMXMaterial*)theStep->GetPreStepPoint()->GetMaterial());
01842 
01843              // Daughter elements
01844              for(std::vector<const G4Track*>::size_type j = 0; j < secondaries->size(); j++)
01845              {
01846                  if(        (*secondaries)[j]->GetDefinition()->GetParticleType()    == "nucleus"
01847                             || (*secondaries)[j]->GetDefinition()->GetParticleName() == "proton" )
01848                  {
01849                      G4Ions* SecondIonsType = dynamic_cast<G4Ions*> ((*secondaries)[j]->GetDefinition());
01850                      double IZAdaughter = 10000 * SecondIonsType->GetAtomicNumber() + 10 * SecondIonsType->GetAtomicMass() + SecondIonsType->GetIsomerLevel();
01851                      run->ParticleCount(IZAdaughter, +1, (TMXMaterial*)theStep->GetPreStepPoint()->GetMaterial());
01852                  }
01853              }
01854          }
01855      }
01856     // Counting of additionnal neutrons not coming from fission
01857     if(Nb_second_neutrons > 1)  // secondary neutrons coming from mother neutron
01858         fEventAction->AddInelasticN(Nb_second_neutrons);
01859     if(Nb_OtherNeutrons > 0) // secondary neutrons coming from mother particle other than neutron
01860         fEventAction->AddInelasticN(Nb_OtherNeutrons);
01861 }
