02000     && theStep->GetTrack()->GetCurrentStepNumber() == 1) { // If first step of a neutron track
02001         if(TMXOption::IsMultiplicationKilled(p->int_Option) // If the multiplication is killed
02002         || theStep->GetTrack()->GetParentID() == 0) {       // or if it is a primary neutron generated by the primaryGenerator => generation = 0
