02013 void SteppingAction::FillHistoSourceN(double energyAtBirth, G4ThreeVector& posAtBirth, double energyAtCounting, G4ThreeVector& posAtCounting, G4ThreeVector& dirAtCounting, int nbNeutrons) {
02014     auto analysisManager = G4AnalysisManager::Instance();
02015     analysisManager->FillH1(16, log10(energyAtBirth), nbNeutrons);
02016     analysisManager->FillH1(17, log10(energyAtCounting), nbNeutrons);
02017     analysisManager->FillH2(32, posAtCounting.x()/mm, posAtCounting.y()/mm, nbNeutrons);
02018     analysisManager->FillH2(33, posAtCounting.x()/mm, posAtCounting.z()/mm, nbNeutrons);
02019     analysisManager->FillH2(34, posAtBirth.x()/mm, posAtBirth.y()/mm, nbNeutrons);
02020     analysisManager->FillH2(35, posAtBirth.x()/mm, posAtBirth.z()/mm, nbNeutrons);
02021     analysisManager->FillH2(36, dirAtCounting.unit().z(), log10(energyAtCounting), nbNeutrons);
02022     analysisManager->FillH2(37, std::acos(dirAtCounting.unit().z())* 360.0 / twopi, log10(energyAtCounting), nbNeutrons);
02023 }
