02031      && theStep->GetTrack()->GetDefinition()->GetParticleName()=="neutron") // it is a neutron
02032     {
02033         ScoreNeutronGeneration(0, 1); // Score 1 source neutron (generation 0)
02034 
02035         G4ThreeVector postPos = theStep->GetPostStepPoint()->GetPosition();
02036         G4ThreeVector postMom = theStep->GetPostStepPoint()->GetMomentumDirection();
02037         FillHistoSourceN(fEnergyAtBirth, fPosAtBirth, KineticEnergy_eV(theStep), postPos, postMom, 1); // Score position / energy of 1 source neutron
02038         fIsSourceNeutron = false; // The neutron has escaped the target
02039     }
