02097 void SteppingAction::CountNeutronGeneration_FromInelastic(int MotherNeutronGeneration, int Nb_inelastic) {
02098     // Counting of additionnal neutrons coming from inelastic reaction (n, Xn)
02099     if(Nb_inelastic > 1 && fIsSourceNeutron == false) {  // Neutron is counted only if it exits the target
02100         // Source neutrons definition 1: The generation of neutrons is not changed after (n, Xn) reactions
02101         if(TMXOption::IsSrcNeutronDefinitionWithNxN(p->int_Option)) { ScoreNeutronGeneration(MotherNeutronGeneration, Nb_inelastic-1); }// +Nb_inelastic-1 neutron from generation MotherNeutronGeneration
02102         else { // The generation of neutrons is changed after (n, Xn) reactions
02103             ScoreNeutronGeneration(MotherNeutronGeneration+1, Nb_inelastic); }// +Nb_inelastic neutron for generation MotherNeutronGeneration+1
02104     }
02105 }
