02107 void SteppingAction::CountNeutronGeneration_FromFission(int NeutronGeneration) {
02108     // Secondary neutron is counted only if it is not a source neutron
02109     if (NeutronGeneration >= 1 && fIsSourceNeutron == false) { ScoreNeutronGeneration(NeutronGeneration, 1); } // +1 neutron from generation fNeutronGeneration+1 (old definition (Energy Amplifier 1995))
02110 }
