02206         else if (processName=="nCapture" && !TMXOption::IsKeffCalculation(p->int_Option)) {
02207             ManageCapture(theStep);}
02208         else if (processName=="neutronInelastic") {
02209             ManageNInelastic(theStep, nNeutrons);}
02210 
02211         ManageNeutronEscape(theStep);
02212 
02214         //  Energy vs Time histogram
02215         //  Fluence Energy spectrum
02217         FillHistoTimeEnergyFlux(theStep, PostStatus == fGeomBoundary, nNeutrons);
02218     }
