02221 void SteppingAction::ManageNeutronBirth(const G4Step * theStep) {
02222     if(theStep->GetTrack()->GetCurrentStepNumber() == 1) { // If first step of the neutron track
02223 
02224         // PreStep quantities
02225         G4ThreeVector PrePos   = theStep->GetPreStepPoint()->GetPosition();
02226         G4double PreGlobalTime = theStep->GetPreStepPoint()->GetGlobalTime()/s;
02227 
