02233 
02234 void SteppingAction::ManageFission(const G4Step* theStep) {
02235 
02236     // Instantiate the analysis manager for the scoring
02237     auto analysisManager = G4AnalysisManager::Instance();
02238 
02239     // Get required quantites from the step
02240     G4Track* theTrack = theStep->GetTrack();
02241     G4int ID_track = -1;
02242     if(theTrack) {ID_track = theTrack->GetTrackID();}
02243     G4double StepLength = theStep->GetStepLength()/cm; // Length of the step in cm
02244     G4double    energyK = KineticEnergy_eV(theStep);
02245     G4double       Tot_Edep = theStep->GetTotalEnergyDeposit()/eV; // Total Energy Deposit in this step in eV
02246     G4double PostGlobalTime = theStep->GetPostStepPoint()->GetGlobalTime()/s; // Time since the event is created
02247     G4ThreeVector   PostPos = theStep->GetPostStepPoint()->GetPosition(); // Position of the postStep point
02248     G4double Threshold_promtNeutrons = 40*ms; // WARNING: hard-coded value, to discriminate between prompt and delayed neutrons
02249 
02250     // Declare a fission at fID_firstfission to estimate energy released by fission and secondaries
02251     if( !fFromFission ) {
02252         fFromFission = true;
02253         fID_firstfission = ID_track;
02254         fEperFission = Tot_Edep;
02255         fNfission_fromFission = 0;
02256     }
02257     ++fNfission_fromFission;
02258 
02259     // Count number of fissions
02260     ++Nfission;
02261     fEventAction->AddFission(1);
02262 
02263     if (fNeutronGeneration == 0) { // If it is a source neutron
02264         fEventAction->AddN0fission(1); // Increase number of fissions due to source neutrons
02265         analysisManager->FillH2(38, PostPos.x(), PostPos.y()); // Score death position of source neutrons
02266         analysisManager->FillH2(45, PostPos.x(), PostPos.z()); // Score death position of source neutrons
02267     }
02268 
02269     // Energy of fission
02270     analysisManager->FillH2(26, std::log10(PostGlobalTime), std::log10(Tot_Edep)); // Score energy and time for fission
02271 
02272     // Energy tot for fission
02273     G4double TotE_fission = Tot_Edep;
02274 
02275     // Number of fission neutrons
02276     const std::vector<const G4Track*> *secondaries = theStep->GetSecondaryInCurrentStep();
02277     for(G4int i=0; i<(G4int)secondaries->size(); ++i) {
02278         G4String secondary_particleName = (*secondaries)[i]->GetDefinition()->GetParticleName();
02279         if (secondary_particleName == "neutron") {
02280             ++Nnfission;  // Increase the counter of neutrons coming from fission
02281 
02282             if ((*secondaries)[i]->GetGlobalTime()/s - PostGlobalTime < Threshold_promtNeutrons) { // prompt neutrons
02283                 fPromptN = true; // This is a prompt neutron
02284                 analysisManager->FillH2(22, std::log10((*secondaries)[i]->GetGlobalTime()/s), std::log10((*secondaries)[i]->GetKineticEnergy()/eV)); // Energy and time for fission secondaries
02285             }
02286             else {
02287                 fPromptN = false; // This is not a prompt neutron
02288                 analysisManager->FillH2(28, std::log10((*secondaries)[i]->GetGlobalTime()/s), std::log10((*secondaries)[i]->GetKineticEnergy()/eV)); // Energy and time for fission secondaries
02289             }
02290         }
02291         else if (secondary_particleName == "gamma") { analysisManager->FillH2(23, std::log10((*secondaries)[i]->GetGlobalTime()/s), std::log10((*secondaries)[i]->GetKineticEnergy()/eV));} // Energy and time for fission secondaries
02292         else if (secondary_particleName == "e-")    { analysisManager->FillH2(24, std::log10((*secondaries)[i]->GetGlobalTime()/s), std::log10((*secondaries)[i]->GetKineticEnergy()/eV));} // Energy and time for fission secondaries
02293         else { analysisManager->FillH2(25, std::log10((*secondaries)[i]->GetGlobalTime()/s), std::log10((*secondaries)[i]->GetKineticEnergy()/eV));} // Energy and time for fission secondaries
02294 
02295         TotE_fission += (*secondaries)[i]->GetKineticEnergy()/eV;
02296     }
02297     analysisManager->FillH2(27, std::log10(PostGlobalTime), std::log10(TotE_fission)); // Energy and time for fission secondaries
02298     fEventAction->Setnu(Nnfission/(double)Nfission);
02299     fEventAction->SetFissionN(Nnfission);
02300 
02301     const G4Isotope* target = GetTargetIsotope(theStep->GetPostStepPoint());
02302     if (target->GetName() == "U235") { fEventAction->AddNabsU5(1.);}  // WARNING: hard-coded value related to the geometry
02303 
02304     // Scoring of transmutation information
02305     if( IsSpentFuel(fPhysicalVolumeNumber) ) {FillNtuple2(PostPos.x()/mm, PostPos.y()/mm, PostPos.z()/mm, energyK, fPhysicalVolumeNumber, 0, StepLength, fmap_Region_volume[fPhysicalVolumeNumber], target->GetN(), target->GetZ());}
02306 
02307     // If the multiplication is not required
02308     if (TMXOption::IsMultiplicationKilled(p->int_Option))
02309         theTrack->SetTrackStatus(fKillTrackAndSecondaries);
02310 
02311     // Score fission in ntuple
02312     FillNtuple1(PostPos.x()/mm, PostPos.y()/mm, PostPos.z()/mm, 0, fPhysicalVolumeNumber, fPhysicalVolumeNumber, 0, 0, 0, 0, theStep->GetTrack()->GetParentID(), 0);
02313 }
02314 
02315 void SteppingAction::ManageCapture(const G4Step* theStep) {
02316 
02317     // Get required quantites from the step
02318     G4ThreeVector PostPos = theStep->GetPostStepPoint()->GetPosition(); // Position of the postStep point
02319     G4double StepLength = theStep->GetStepLength()/cm; // Length of the step in cm
02320     G4double    energyK = KineticEnergy_eV(theStep);
02321 
02322     // Get the number of neutrons absorbed or captured by U-235 and U-238 respectively
02323     const G4Isotope* target = GetTargetIsotope(theStep->GetPostStepPoint());
02324     if     (target->GetName() == "U235")  // WARNING : hard coded value related to the geometry
02325         fEventAction->AddNabsU5(1.);
02326     else if(target->GetName() == "U238")  // WARNING : hard coded value related to the geometry
02327         fEventAction->AddNcapU8(1.);
02328 
02329     // Count neutron absorption
02330     fEventAction->AddNabsTot(1);
02331 
02332     // Score death position of source neutrons
02333     if (fNeutronGeneration == 0) {
02334         auto analysisManager = G4AnalysisManager::Instance();
02335         analysisManager->FillH2(38, PostPos.x(), PostPos.y());
02336         analysisManager->FillH2(45, PostPos.x(), PostPos.z());
02337     }
02338 
02339     // If sample to study the transmutation
02340     if( IsSpentFuel(fPhysicalVolumeNumber) ) { FillNtuple2(PostPos.x()/mm, PostPos.y()/mm, PostPos.z()/mm, energyK, 0, fPhysicalVolumeNumber, StepLength, fmap_Region_volume[fPhysicalVolumeNumber], target->GetN(), target->GetZ());}
02341 
02342     // Score capture in ntuple
02343     FillNtuple1(PostPos.x()/mm, PostPos.y()/mm, PostPos.z()/mm, 0, fPhysicalVolumeNumber, 0, fPhysicalVolumeNumber, 0, 0, 0, theStep->GetTrack()->GetParentID(), 0);
02344 }
02345 
02346 void SteppingAction::ManageNInelastic(const G4Step* theStep, int& nNeutrons) {
02347 
02348     // Get required quantites from the step
02349     G4ThreeVector PostPos = theStep->GetPostStepPoint()->GetPosition(); // Position of the postStep point
02350     G4double StepLength = theStep->GetStepLength()/cm; // Length of the step in cm
02351     G4double    energyK = KineticEnergy_eV(theStep);
02352     G4int InelasticVol = 0, nXnVol = 0, nnPrimeVol = 0;
02353 
02354     // Get target isotope (nucleus which interacts with neutron)
02355     const G4Isotope* target = GetTargetIsotope(theStep->GetPostStepPoint());
02356     nNeutrons = 0; // Initialize number of secondary neutrons to 0
02357 
02358     // Counter number of secondary neutrons from inelastic
02359     const std::vector<const G4Track*> *secondaries = theStep->GetSecondaryInCurrentStep();
02360     for(G4int i=0; i<(G4int)secondaries->size(); ++i) {
02361         if ((*secondaries)[i]->GetDefinition()->GetParticleName() == "neutron") { ++nNeutrons; }
02362     }
02363 
02364     if(nNeutrons == 0) { // Neutron is absorbed => usually called inelastic reaction (e.g. (n, alpha) reaction)
02365         InelasticVol = fPhysicalVolumeNumber;
02366 //         if(IsSpentFuel(fPhysicalVolumeNumber)) { InelasticVol += theStep->GetPostStepPoint()->GetPhysicalVolume()->GetCopyNo(); } // if volume = pSpentFuel -> element to be transmuted
02367 
02368         // Death position of source neutrons
02369         if (fNeutronGeneration == 0) {
02370             auto analysisManager = G4AnalysisManager::Instance();
02371             analysisManager->FillH2(38, PostPos.x(), PostPos.y());
02372             analysisManager->FillH2(45, PostPos.x(), PostPos.z());
02373         }
02374 
02375         // If sample to study the transmutation
02376         if( IsSpentFuel(fPhysicalVolumeNumber) ) {FillNtuple2(PostPos.x()/mm, PostPos.y()/mm, PostPos.z()/mm, energyK, 0, InelasticVol, StepLength, fmap_Region_volume[fPhysicalVolumeNumber], target->GetN(), target->GetZ());}
02377     }
02378     else if(nNeutrons == 1) { // (n, n') reaction
02379         nnPrimeVol = fPhysicalVolumeNumber;
02380 //         if(IsSpentFuel(fPhysicalVolumeNumber)) { nnPrimeVol += theStep->GetPostStepPoint()->GetPhysicalVolume()->GetCopyNo(); } // if volume = pSpentFuel -> element to be transmuted
02381     }
02382     else { // nNeutrons > 1 => (n, Xn) reaction
02383         nXnVol = fPhysicalVolumeNumber;
02384 //         if(IsSpentFuel(fPhysicalVolumeNumber)) { nXnVol += theStep->GetPostStepPoint()->GetPhysicalVolume()->GetCopyNo(); } // if volume = pSpentFuel -> element to be transmuted
02385 
02386         // Death position of source neutrons
02387         if (fNeutronGeneration == 0) {
02388             auto analysisManager = G4AnalysisManager::Instance();
02389             analysisManager->FillH2(38, PostPos.x(), PostPos.y());
02390             analysisManager->FillH2(45, PostPos.x(), PostPos.z());
02391         }
02392 
02393         // If sample to study the transmutation
02394         if( IsSpentFuel(fPhysicalVolumeNumber) ) {FillNtuple2(PostPos.x()/mm, PostPos.y()/mm, PostPos.z()/mm, energyK, 0, nXnVol, StepLength, fmap_Region_volume[fPhysicalVolumeNumber], target->GetN(), target->GetZ());}
02395     }
02396 
02397     // WARNING: hard-coded values to count the absorption/capture in U-235 or U-238
02398     if(nNeutrons != 1 && target->GetName() == "U235") // If absorption in U235
02399         fEventAction->AddNabsU5(1.);
02400     if(nNeutrons == 0 && target->GetName() == "U238") // If capture in U235
02401         fEventAction->AddNcapU8(1.);
02402 
02403     // Count neutron total absorption
02404     if(nNeutrons == 0)
02405         fEventAction->AddNabsTot(1);
02406 
02407     // Score inelastic in ntuple
02408     FillNtuple1(PostPos.x()/mm, PostPos.y()/mm, PostPos.z()/mm, 0, fPhysicalVolumeNumber, 0, 0, InelasticVol, nnPrimeVol, nXnVol, theStep->GetTrack()->GetParentID(), 0);
02409 }
02410 
02411 void SteppingAction::ManageNeutronEscape(const G4Step* theStep) {
02412     if (theStep->GetPostStepPoint()->GetStepStatus() == fWorldBoundary
02413         && theStep->GetTrack()->GetDefinition()->GetParticleName() == "neutron")
02414     {
02415         G4ThreeVector PrePos = theStep->GetPreStepPoint()->GetPosition();
02416         FillNtuple1(PrePos.x()/mm, PrePos.y()/mm, PrePos.z()/mm, -1, fPhysicalVolumeNumber, 0, 0, 0, 0, 0, theStep->GetTrack()->GetParentID(), -1);
02417 
02418         // Kill the neutron
02419         theStep->GetTrack()->SetTrackStatus(fStopAndKill);
02420     }
02421 }
02422 
02423 const G4Isotope* SteppingAction::GetTargetIsotope(G4StepPoint* thePostPoint) {
02424     G4VProcess* process = const_cast<G4VProcess*>(thePostPoint->GetProcessDefinedStep());
02425     G4HadronicProcess* hproc = dynamic_cast<G4HadronicProcess*>(process);
02426     const G4Isotope* target = NULL;
02427     if (hproc) target = hproc->GetTargetIsotope();
02428 
02429     return target;
02430 }
